DROP TABLE IF EXISTS `change_log`;
CREATE TABLE `change_log` ( `id` INT(10) NOT NULL PRIMARY KEY, `version_id` INT(10) NULL DEFAULT '1', `change_type` INT(10) NULL DEFAULT '1' , `change_obj_id` INT(10) NULL DEFAULT NULL , `change_obj_name` VARCHAR(50) NULL DEFAULT NULL, `is_new` TINYINT(4) NULL DEFAULT '0', `change_log` TEXT NULL );


