INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (1, '船长', '', 'kunkka', 'coco', 'kunkka.gif', 'display_kunkka.jpg', '', '近卫', 'GANK', '难', 'XET三连 水刀压制 预判洪流与幽灵船 无法使用补刀斧', 549, 234, 47, 57, 128, 1.70, 0.0, 0.0, 300, 2, 'STR', 24, 14, 18, 2.7, 1.3, 1.5, 7, 6, 7, 2, 7, '在近位的所有盟军中，只有一个人，在许多大陆上都有着不同但又令人生畏的名字。他的追随者们叫他COCO船长，有的人叫他杰克·斯帕罗，还有的人叫他传说中的水手。但只有极少数的人知道他的真名和含义。他可以驾御和施展洪流的力量，将它们象喷泉一样涌起从而将他们的敌人扔向天空。他华丽的刀锋可以接受大海的庇护，因此它的舞动就如同汹涌的潮水。通过一种以水为形式的空间魔法，他可以把你带回你以前的位置。现在船长驶向了天灾大陆，同时随身带着他那可以激发他和他的战友战斗意志的传说中的朗姆酒。他将为近位军团的胜利铺平道路。他就是KUNKKA，七大洋的统帅。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (2, '兽王', '', 'Rexxar', 'Bm', 'bm.gif', 'display_bm.jpg', '', '近卫', 'GANK', '难', '多线操作 野猪粘人神鹰控制视野 GANK转推进', 587, 208, 56, 60, 100, 1.70, 0.0, 0.0, 310, 5, 'STR', 23, 18, 16, 2.2, 1.6, 1.9, 3, 4, 8, 5, 4, 'Rexxar来自传说中具有半兽半食人魔的血统的Mok`nathal氏族。原本是个四处漂泊的浪人，但为了更好的保护他身边的大自然，Rexxar还是提着他那两把充满力量的斧头加入了近卫军团。作为野兽的朋友，Rexxar善于用他那令人不安的野性撕扯敌人，而且为了胜利，他甚至会将自己的斧头往敌人身上扔去。兽王Rexxar是天底下真正的勇士，并且在需要他的时候随时都可以挺身而出。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (3, '半人马酋长', '', 'Bradwarden', 'Cent', 'cent.gif', 'display_cent.jpg', '', '近卫', '肉盾', '中等', '反击抗压 跳刀踩地板 双刃剑最后使用', 587, 195, 55, 57, 100, 1.70, 0.0, 0.0, 305, 3, 'STR', 23, 15, 15, 2.6, 2.0, 1.6, 3, 3, 6, 2, 8, '即使以半人马的标准来衡量，他们的族长Bradwarden的身材仍然称得上是“远古巨兽”。即使他的同胞们都已被天灾军团腐化而变节但Bradwarden却依然谨守着他的尊严不为所动。不得不依靠打倒自己的同胞才能逃出生天，这使得他变得愤怒而无情，他矢志要向天灾军团复仇。现在，他就像一座不可击倒的山那样屹立着，而他的敌人随着他手中挥舞着的巨大战斧纷纷倒地。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (4, '撼地神牛', '', 'Raigor Stonehoof', 'Es', 'es.gif', 'display_es.jpg', '', '近卫', '团控', '难', '沟壑封路 跳刀3连 蹲坑保后期', 568, 208, 46, 56, 128, 1.70, 0.0, 0.0, 300, 3, 'STR', 22, 12, 16, 2.5, 1.4, 1.8, 4, 6, 7, 6, 4, '在贫瘠之地，传说有群牛头人贤者，在此获取大地的精华。他们的思想与他们脚下的这片褐土地结合在一起。他们的精神力能引导土壤的流向。这些战士仅凭一时兴起就能制造出深深的沟壑，因此让人感到畏惧。他们的力量十分强大，以至于每次使用魔法时都会对大地造成强烈的震动。可悲的是这些贤者们却神秘的淡出了这片大陆。当世界之树被威胁之时，信使们找到了这个强大的种族的族人，但只找到一个。撼地神牛，这个技艺娴熟的年轻牛头人，寻求着战斗，以创造新的传奇。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (5, '全能骑士', '', 'Purist Thunderwrath', 'OK', 'ok.gif', 'display_ok.jpg', '', '近卫', '治疗', '中等', '加血并造成范围伤害 注意取消施法后摇 给核心队友魔免 大招无视物理攻击', 530, 221, 51, 61, 128, 1.70, 0.0, 0.0, 300, 4, 'STR', 20, 15, 17, 2.6, 1.8, 1.8, 2, 4, 4, 8, 4, '作为神的使者，Purist能够借助神圣力量使用各种支援与防御技能。他竭尽全力地保护和强化他的盟友，不过当必要的时候，他也能亲身投入到攻击和阻碍的作战中。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (6, '熊猫酒仙', '', 'Mangix', 'Panda', 'panda.gif', 'display_panda.jpg', '', '近卫', '肉盾', '中等', '风熊猫疾风步吹风接地熊猫晕 前期点黄点 熊猫复活优先级 地>风>火 酒雾克制DPS', 587, 182, 52, 59, 128, 1.70, 0.0, 0.0, 300, 3, 'STR', 23, 16, 14, 2.9, 2.0, 1.2, 7, 5, 7, 4, 8, '拥有着常人无法企及的反射神经，Mangix有效地挥舞他的橡木棍打击对手。他是最难以杀死的英雄之一，他的攻击迅捷而有力。他已经实在无法计算到底有多少人倒在了他的脚下。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (7, '流浪剑客', '', 'Sven', 'Sven', 'sven.gif', 'display_sven.jpg', '', '近卫', 'GANK', '简单', 'T+S假锤骗技能 锤子范围AOE效果', 587, 182, 54, 56, 128, 1.70, 0.0, 0.0, 295, 2, 'STR', 23, 21, 14, 2.7, 2.0, 1.3, 7, 3, 7, 2, 4, '伟大的秩序之神的信徒，圣骑士Sven发誓要维护芸芸众生的权力。作为骑士和暗夜精灵的后代，Sven一出生就被放逐，从此在独处中冥想和训练自己。在流浪的过程中他决定了要保护无辜的人免受邪恶的侵犯。带着受祝福的大剑“正义”，以神之力量击倒对手，任何Sven所看到的不公正都将感受到他的愤怒。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (8, '山岭巨人', '', 'Tiny', 'Tiny', 'tiny.gif', 'display_tiny.jpg', '', '近卫', 'GANK', '中等', 'VT二连 跳刀必出 利用投掷来追击或者逃生', 606, 182, 61, 67, 128, 1.70, 0.0, 0.0, 285, 0, 'STR', 24, 9, 14, 3.0, 0.9, 1.6, 5, 4, 8, 2, 7, '由Ashenra嶙峋的山脉孕育而生，Tiny可以通过投掷大量的泥石造成山崩，也能够将任何东西抛向空中，这都显示了他不起眼的身形下其实隐藏着巨人般的力量。他岩石般坚硬的外表，让攻击他的敌人反受其害。随着时间的推移，Tiny身体中心的磁石能够吸引四周的石头土块，让Tiny“发身长大”，成为名副其实的山岭巨人。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (9, '牛头人酋长', '', 'Tauren Chieftain', 'Tc', 'tc.gif', 'display_tc.jpg', '', '近卫', '团控', '难', '后手CTE三连 贴身光环增加伤害 必要时出黑皇保证控制', 606, 299, 47, 57, 150, 1.70, 0.0, 0.0, 315, 3, 'STR', 24, 14, 23, 2.3, 1.5, 1.6, 6, 4, 5, 2, 7, '自古以来，牛头人部落长老们就有一个流传已久的预言，那就是部落会迎来一位力量甚比最强大战士、智慧超越最年老萨满的酋长。多年以来，漫游于部落人民居住的神圣之地，一直向先祖寻求的酋长，终于有一天证明了--他，Cairne Bloodhoof,就是那位预言中的英雄。他的体内寄宿着最强大先祖的灵魂，现在，大地会因酋长和他先祖守护之魂的联合之力而颤抖。任何手段都不能削减先祖守护之魂那魔法之戟的破坏力。运用他无与伦比的力量，酋长甚至可以挥动战斧劈裂大地。天灾军团将很快感受到，为了失落家园而复仇的牛头人酋长的愤怒。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (10, '树精卫士', '', 'Rooftrellen', 'TP', 'tp.gif', 'display_tp.jpg', '', '近卫', '团控,肉盾', '简单', '隐身先手与逃生 大招控制效果无视魔免', 625, 221, 73, 81, 128, 1.90, 0.0, 0.0, 300, 1, 'STR', 25, 15, 17, 2.8, 2.0, 1.8, 4, 2, 5, 6, 7, '在燃烧军团入侵的时候，树精卫士还只是棵幼小的树苗，而如今，他已成为经历了无穷岁月的树人智者。经过数千年的成长，现在他正在为近卫军团贡献他的力量。借助伟大的森林，树精卫士可以运用森林之力隐藏他的盟友，他也可以制造藤蔓护甲给予己方以充分的保护，他还可以用枝蔓缠绕住对手，然后挥舞他沉重的树干将对手置于死地。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (11, '精灵守卫', '', 'Guardian Wisp', 'Wisp', 'wisp.gif', 'display_wisp.jpg', '', '近卫', '辅助', '难', '传送支援与救人 利用链接回复及控制 收放幽光之魂开视野', 473, 299, 43, 52, 575, 1.70, 0.0, 0.0, 295, 0, 'STR', 17, 14, 23, 1.9, 1.6, 1.7, 3, 2, 4, 8, 1, '埃欧与它的族人们，作为低等精灵族中的一员，一直都平静的生活在森林里。随着大战的打响，生活在森林中的生物被迫拿起武器，守卫它们的家园。最初，埃欧并不知道它的使命何在，而当它发现它可以通过生命之力将自己与盟友连接，强化他们的力量或者是帮助他们躲避来自敌人的伤害。埃欧，精灵族守卫，担负起了它应承担的责任。精灵族人们发誓为它而战，因此埃欧可以在战场上随时随刻的呼唤它们来帮助自己。埃欧是为了包围它的族人而参战，而不是因为近卫与天灾之间永无止境的仇恨。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (12, '炼金术士', '', 'Razzil Darkbrew', 'Alch', 'alch.gif', 'display_alch.jpg', '', '近卫', '后期', '中等', '魂戒无限雾 大招躲技能 大招回血速度极快', 625, 325, 49, 58, 128, 1.70, 0.0, 0.0, 295, 2, 'STR', 25, 11, 25, 1.8, 1.2, 1.8, 7, 6, 4, 2, 8, '作为地精炼金术士里精英中的精英，Razzil受雇于近卫军团开发各种对抗天灾部队的化学武器。当亲自投入战斗时，他以一头用作实验品的巨型食人魔作为坐骑。那些投下的实验用的化学药剂，足以将食人魔这样已经非常难缠的对手变为最可怕的怪物。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (13, '发条地精', '', 'Rattletrap', 'CG', 'cg.gif', 'display_cg.jpg', '', '近卫', 'GANK', '难', '钩子提前量较小 导弹开视野 利用能量齿轮控制及逃生', 606, 221, 55, 57, 128, 1.70, 0.0, 0.0, 315, 2, 'STR', 24, 13, 17, 2.7, 1.2, 1.3, 3, 4, 8, 3, 6, '发条地精是旧战时期地精武器工业的副产品，在战后被弃置多年，直到近卫军团发现并重新启动了他。作战方面，发条地精的能力无可挑剔，他体内储藏的各式各样的弹药能轻而易举消灭任何方位的敌人。他标志性的铁钳能如索爪般伸长，即使针尖大小的东西，也手到擒来。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (14, '龙骑士', '', 'Knight Davion', 'Dk', 'dk.gif', 'display_dk.jpg', '', '近卫', '后期', '中等', 'shift分身斧三冰龙 强点控', 511, 195, 46, 52, 128, 1.70, 0.0, 0.0, 290, 4, 'STR', 19, 19, 15, 2.8, 2.2, 1.7, 8, 5, 4, 2, 6, 'Davion是龙族和人类通婚的产物。藉着变身为龙的能力，对于他的敌人来说，Davion简直是头凶恶的猛兽。即使在人类形态下，他身上的龙族血统也赋予他能力成为异常强壮而且攻击力强大的战士。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (15, '神灵武士', '', 'Huskar', 'Sw', 'sw.gif', 'display_sw.jpg', '', '近卫', 'gank', '简单', '对线法球压制 在对面无技能时红血激情 大招会对自己产生散失效果 沸血之矛无法自杀', 492, 234, 39, 48, 400, 1.60, 0.0, 0.0, 300, 2, 'STR', 18, 20, 18, 2.4, 2.4, 1.5, 4, 4, 5, 2, 4, '暗矛部落的巨魔们在很久以前就被从荆棘谷的原住地流放出来，他们是大多数人眼中最野蛮的种族，与他们相比，甚至连战场都显得优雅起来。这个“美誉”，更因他们在近卫军团的代表，那个为了将敌人烧成碎片而不惜燃烧自己生命的Huskar，而名声大噪。在他的族人眼里，身为神灵武士的他，是被神祝福的烈士，不畏死亡，永远向前，甚至是疯狂的，誓要将天灾不死拖入坟墓。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (16, '刚背兽', '', 'Rigwarl', 'Bb', 'bb.gif', 'display_bb.jpg', '', '近卫', '肉盾', '简单', '把背留给敌人 团战冲进去打乱阵型 保持自己身上的战意 R可以用来补刀', 568, 182, 52, 62, 128, 1.70, 0.0, 0.0, 295, 3, 'STR', 22, 17, 14, 2.2, 1.8, 2.8, 3, 3, 4, 2, 8, 'Rigwarl是部族中久经考验的真正的斗士。他带领部族保卫家园，然而面对天灾军团潮水般的猛攻下不幸失败。随着战况日下，他已经无力阻止天灾军团的集结，最后只得答应加入近卫军团。凭借他得天独厚的素质以及经过大自然净化的力量，Rigwarl的战斗方式非常残忍，使之成为战争中最不拘形式和最特立独行的一员。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (17, '火凤凰', '', 'Phoenix', 'lcarus', 'lcarus.gif', 'display_lcarus.jpg', '', '近卫', 'GANK', '中等', '终于脱离无节操行列了', 511, 234, 45, 55, 500, 1.70, 0.0, 0.0, 285, 0, 'STR', 17, 12, 18, 2.9, 1.3, 1.8, 7, 4, 7, 2, 4, '远古圣典提及光的七道神圣射气，在辐合时发生强烈的冲突。这些道射气的碰撞在凡人的国度创造出一个最纯净的火堆，并从中诞生了神话般的凤凰。就像天赐之火显现的那样，伊卡洛斯既能发出死亡与毁灭之炎，又能用来治疗与重生。从他体内射出的高温光线，可以轻易焚化战场上的整支部队。为了征服他的敌人，伊卡洛斯甚至愿意释放全部的生命力，利用太阳能量创造出毁灭天地的大爆炸。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (18, '巨牙海民', '', 'Tuskarr', 'Ymir', 'ymir.gif', 'display_ymir.jpg', '', '近卫', 'gank', '难', 'E封路 用W与人马等队友GANK', 587, 234, 50, 54, 128, 1.70, 0.0, 0.0, 305, 3, 'STR', 23, 23, 18, 2.3, 2.1, 1.7, 7, 3, 8, 2, 6, '尤弥尔来自于北方冻原，是最聪明的巨牙海民战士之一。他所居住的村庄在他青年时期不断遭受冰原巨魔和地穴蜘蛛的劫掠，在常年的抵抗下他积累了众多的战争经验，因此尤弥尔自愿加入近卫军团的队列，以协助抵挡日益强大又臭名昭著的巫妖王。藉由冰元素专精的操控能力，尤弥尔可以用冰霜魔法出其不意的打击敌人。他的武器不只有沉重又迅速的大雪球，飞快投掷的锋利冰片，还有魔术般的大暴雪极大的削弱敌人的战斗能力，当然，轻视他那强健身躯的爆发力，也是愚蠢之极的。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (19, '军团指挥官', '', 'Tresdin, Legion Commander', 'LC', 'lc.gif', 'display_lc.jpg', '', '近卫', '后期', '中等', '决斗沉默效果等于末日大招 强攻解控制 破召唤物推进', 644, 260, 57, 61, 128, 1.70, 0.0, 0.0, 310, 3, 'STR', 26, 18, 20, 2.6, 1.7, 2.2, 8, 7, 6, 3, 7, '我叫特斯丁，我们王国内部现在正滋生着一股巨大的威胁。这个巨大的威胁就是那一大群肮脏不堪的非人类难民们。他们让我们的大道污秽不堪，他们的杂种语言四处可闻!正因为整个王国都看到我的领地被这样一群杂种侵蚀，所以我才没能成为最伟大的将军。但这一切，将在不久之后结消失。因为我的战斧，已经向那些威胁到人类领土的黑暗部落砍下。我已经出发，去征服那些恐怖之物。谁，敢与我一决生死!我将让世界走上正轨，人类的正轨!');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (20, '地精撕裂者', '', 'Goblin Shredder', 'GS', 'gs.gif', 'display_gs.jpg', '', '近卫', 'GANK', '中等', '劣势路单好手 多找树林打架 利用钩爪切入与逃生 开战前用小兵攒活性护甲', 568, 273, 48, 52, 128, 1.70, 0.0, 0.0, 290, 0, 'STR', 22, 21, 16, 2.1, 1.8, 1.3, 4, 7, 7, 4, 8, '在遗忘森林的深处有着众多宝藏。没有人知道这件事情，也没有人意识到地精发明家瑞兹拉克的小小身体有多么的危险。瑞兹拉克通过将重型武器和能轻易砍倒一大片森林的伐木机结合，创造出了一个既致命又挣钱的伐木机。有着可拆分的锯刃，锋利的刀沿，能勾住树木的锁链，瑞兹拉克消灭敌人就如同砍伐一棵小树苗那样简单。尽管瑞兹拉克谈不上高尚，但是近卫军团总是会付给他足够的金钱去完成任务，当然前提是他和他的伐木机得离生命古树远点！');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (21, '斧王', '', 'Mogul Kahn', 'axe', 'axe.gif', 'display_axe.jpg', '', '天灾', '肉盾', '中等', '斩杀之前看对面血量 对线时点对面英雄吸引 嘲讽无视魔免', 625, 182, 49, 53, 128, 1.70, 0.0, 0.0, 290, 2, 'STR', 25, 20, 14, 2.5, 2.2, 1.6, 3, 6, 7, 2, 7, '在很久以前的人兽大战中，许多半兽人战士受到恶魔之力的影响而腐化沦落为黑暗势力的工具。其中就有血斧部落的首领，Mogul Kahn。他原被人类圣骑士所杀，但是在Mannaroth指挥半兽人向Archimonde进攻的时候，人们却不可思议地看到他出现在天灾军团的阵营中。他被恶魔复活，运用他迅捷的反应和强大的攻击力猎杀近卫军团的战士，他残忍地将对手砍为数块，他甚至能使敌人变的和他一样的对杀戮充满着邪恶渴望。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (22, '混沌骑士', '', 'Nessaj', 'ck', 'ck.gif', 'display_ck.jpg', '', '天灾', 'gank,后期', '中等', '控制与伤害不稳定 需要强力回蓝弥补蓝耗', 530, 208, 49, 79, 128, 1.70, 0.0, 0.0, 325, 4, 'STR', 20, 14, 16, 2.9, 2.1, 1.2, 7, 5, 7, 2, 5, '作为混乱之神的弟子，Nessaj极力运用他被赋予的力量散播混乱和邪恶。他竭尽全力对抗着秩序和善良。他可以通过召唤混乱能量来伤害并随机击晕对手，再配合他撕裂空间的能力，瞬间击倒一切敢于阻碍他的对手。他可怕力量的极致在于他可以制造三个自身的幻象，每个幻象都拥有本体全部的攻击力，在战场上制造大范围的混乱，给正义以迎头痛击。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (23, '末日使者', '', 'Lucifer', 'Doom', 'doom.gif', 'display_doom.jpg', '', '天灾', 'gank', '难', '大招无视魔免 留点看对面等级加等级炮 根据需要选择野怪能力', 644, 169, 53, 69, 150, 1.70, 0.0, 0.0, 290, 0, 'STR', 26, 11, 13, 3.2, 0.9, 2.1, 5, 6, 7, 3, 6, 'Lucifer曾经是黑暗领主萨格拉斯带领燃烧军团大举入侵时末日卫队的统领，在他的旧主离开多时的今天，他将他不灭的忠诚献给天灾军团以完成主人未了的心愿。他贪婪地吞噬那些弱小的生命，同时击碎那些勉强能够抵御炙热焦土的灵魂。他从不留活口，即使是那些终于忍受住了他的怒火的人也将一个个在他的末日诅咒下痛苦地死去。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (24, '噬魂鬼', '', 'N`aix', 'Naix', 'naix.gif', 'display_naix.jpg', '', '天灾', '后期', '中等', '狂暴魔免躲技能 吸血能和其他物品叠加 利用大招逃生及GANK', 625, 195, 57, 67, 128, 1.70, 0.0, 0.0, 315, 2, 'STR', 25, 18, 15, 2.4, 1.9, 1.8, 8, 3, 7, 2, 7, '曾经在Lordaeron有一个贪婪到已经不值得任何怜悯的人。终于，在试图偷取Arthas王子的圣戒时被抓获并被判绞刑。他因盗窃的欲望而腐蚀的身体后来以食尸鬼的形态复生。他已经不记得自己的名字了，只知道现在自己是N`aix，欲望和贪婪的化身。他现在从他遇见的每一个生物那里贪婪的偷取生命。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (25, '地狱领主', '', 'Abaddon', 'LOA', 'loa.gif', 'display_loa.jpg', '', '天灾', '肉盾', '易', '无光之盾解负面状态 死亡缠绕可以自杀 回光返照解负面状态', 587, 273, 55, 65, 128, 1.70, 0.0, 0.0, 300, 2, 'STR', 23, 17, 21, 2.7, 1.5, 2.0, 2, 3, 4, 7, 6, 'Abaddon曾经是一个有名望的圣骑士，但在燃烧军团入侵的时候堕入黑暗。他的力量和灵魂都被腐蚀，因此变得和魔鬼一样疯狂。握着巫妖王为他准备的吸魂剑，他纵马而出，即使明知没有成功的可能，也从来不会在杀掉他的目标之前收手。身为地狱领主的他，不但可以通过他的黑暗能量盾来增强自己的韧性，而且可以把伤害当成小事一般不予理睬。因此，他可以带领着天灾军团永远向前，永远出现在与正义势力的战斗中，即使他的敌人曾经是他最好的同伴。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (26, '狼人', '', 'Banehallow', 'Wolf', 'wolf.gif', 'display_wolf.jpg', '', '天灾', '后期', '中等', '多线操作 大招期间所有可控单位522移速 小狼追击卡位与隐身侦查 可以出门打野', 568, 195, 53, 57, 128, 1.70, 0.0, 0.0, 305, 1, 'STR', 22, 16, 15, 2.8, 1.9, 1.6, 7, 6, 5, 3, 5, 'Banehallow是天灾军团创造出来的狂兽魔神。他曾经在黑森林居住了几年，以猎杀在那里迷路的无辜生命为乐。现在他被召去援助他的主人。之后他便沉溺于鲜血和战斗。他的狼人血统不但可以吸引野兽幽魂加入他的队伍，而且可以将他那能够撕碎血肉的强壮气魄渗透到盟友的身体中去。他的身体更是超乎常理的快速和灵活。甚至有些人说他能变成一头巨狼，让他的耐力和速度更为惊人，使他在追杀敌人的时候更加毫无顾忌。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (27, '暗夜魔王', '', 'Balanar', 'NS', 'ns.gif', 'display_ns.jpg', '', '天灾', 'gank', '中等', '一级V补兵 夜晚有大就开', 587, 208, 57, 61, 128, 1.70, 0.0, 0.0, 295, 6, 'STR', 23, 18, 16, 2.8, 2.2, 1.6, 5, 3, 8, 2, 5, '你对夜晚感到好奇是么？那你当心了。你所向前迈出的每一步，都让你在黑暗的恐怖统治中越陷越深。暗夜魔王，作为向世界散播扭曲虚空绝对黑暗的恐惧领主中的精英，在黑暗中神出鬼没。你会心惊肉跳，你会双腿颤抖，你会不知所措地面对他在黑暗中的追猎。你没有反抗的可能，你没有逃跑的可能，你甚至没有藏身之处——黑暗无所不在！祈祷吧，哀嚎吧，但是黑暗不会过去，直到你最终死去。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (28, '深渊领主', '', 'Azgalor', 'PL', 'pl.gif', 'display_pl.jpg', '', '天灾', '推进', '中等', '多用大招进行团队传送 尽量搭配推进英雄', 625, 221, 62, 68, 128, 1.70, 0.0, 0.0, 305, 4, 'STR', 25, 12, 17, 2.6, 1.3, 2.6, 3, 8, 4, 5, 6, '恶魔Azgalor是魔王阿克蒙德手下大将之一，在燃烧军团败于卡利姆多后，被困于物质位面上。对破坏的热爱，使得他略怀对旧主的歉意，便接受了巫妖王的邀请，帮助他带领天灾军团去征服一切。毕竟，这个深渊领主在这个时候是最开心的。召唤大量火焰，从敌人的头顶上倾斜，并在他们被烤成尸体后，将他们挨个儿引爆。像他那样的怪物，掌握着如此可憎的力量，当他在战场上出现时，无论引起多么强烈的恐惧感，也不足为奇了。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (29, '屠夫', '', 'Pudge', 'Pudge', 'pudge.gif', 'display_pudge.jpg', '', '天灾', 'gank', '难', '阴影钩 计算提前量 弯钩加长钩 熟记常用站位 钩子救队友', 625, 182, 52, 58, 128, 1.70, 0.0, 0.0, 285, 1, 'STR', 25, 14, 14, 3.2, 1.5, 1.5, 3, 3, 8, 3, 5, 'Pudge在战场上徘徊着，诠释着残忍与死亡。他不停地寻找着鲜活的血肉，用来黏到他那可怕的躯体上去。年复一年，他的皮肤已经坚硬到足以抵抗魔法了，而且还能施放毒气，极度有效地弄死敢于靠近他的勇士们。他的钩子能将敌人拽进他那巨大的胃中去，而他的胃只需要短短的几秒钟，就能将其完全消化掉。所以，无论是敌人甚至是盟友，都会对屠夫产生一种难以言状的恐惧。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (30, '骷髅王', '', 'King Leoric', 'SNK', 'snk.gif', 'display_snk.jpg', '', '天灾', '后期', '易', '计算好大招蓝量再扔锤子 新手推荐', 568, 169, 54, 56, 128, 1.70, 0.0, 0.0, 300, 4, 'STR', 22, 18, 13, 2.9, 1.7, 1.6, 6, 2, 6, 2, 9, '他曾经是一个保家卫国的高贵骑士，然而不幸被投入地狱，经历了数世纪的折磨。现在，Lucifer将这具腐烂的躯体连同他麻木的灵魂一起抛回尘世，作为Leoric王。他以毫不迟疑的目光带领着手下向前挺进，他只知道一件事情----服从黑暗之王本人的命令。他能使用强大的魔法锤使对手瘫痪，他的大剑能让他和他的手下吸取敌人的血液，在战场上他总是令人望而生畏。据说他是不死之身，然而被他的战锤击倒的人却再也无法站起来。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (31, '鱼人守卫', '', 'Slardar', 'Slarder', 'slarder.gif', 'display_slarder.jpg', '', '天灾', 'gank', '中', '被动晕无视魔免 给每个人都点灯 追杀冲击阵型强力', 549, 195, 51, 59, 128, 1.70, 0.0, 0.0, 300, 5, 'STR', 21, 17, 15, 2.8, 2.4, 1.5, 7, 4, 6, 4, 6, '被困在大漩涡的激流之底上千年以后，Slardar从混沌的深海中浮升，和他的同胞一同为强大的恶魔领主伊利丹-怒风卖命。这个娜迦战士在近乎盲目的狂热支撑下勇猛战斗，直到在战场中心屈服于致命的伤口而倒下。当他的命运和那位冷酷无情的巫妖王联系到一起时，他的重生看起来也就合情合理了。重生后的鱼人守卫作为一只没有灵魂和感情的野兽，以超乎寻常的速度撕裂敌人的队列，并用巨力击昏他们，从不气馁地去满足巫妖王的每一次心血来潮。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (32, '不朽尸王', '', 'Dirge', 'UD', 'ud.gif', 'display_ud.jpg', '', '天灾', '推进,肉盾', '中等', '注意墓碑摆放位置 以线上团战为主', 568, 351, 54, 62, 128, 1.70, 0.0, 0.0, 310, 3, 'STR', 22, 10, 27, 2.1, 0.8, 2.0, 4, 5, 4, 5, 9, '每个遇到尸王Undying都认为他是个格外丑陋和恶心的标本，包括他的僵尸随从们。他曾在荒野上游荡，最终选择侍奉巫妖王，是因为他憧憬着有个能够承认和接纳他的地方。虽然他仍然被天灾军团的其他人所厌恶，但他那可怕的能力：包括召唤具有自我复制能力的小型食尸鬼，以及在敌军中快速的散播瘟疫，其强大的威力都是无可非议的，并且随时有可能成为天灾军团的一把利器，来使他们的邪恶远征，达到一击必杀的效果。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (33, '潮汐猎人', '', 'Leviathan', 'Th', 'th.gif', 'display_th.jpg', '', '天灾', '团控', '易', '需要提升蓝上限 团战大招先手或反先手', 568, 208, 47, 53, 128, 1.70, 0.0, 0.0, 310, 3, 'STR', 22, 15, 16, 3.0, 1.5, 1.7, 3, 3, 7, 2, 6, '大海的力量让人既敬且畏，而Leviathan正是这种力量的化身。他坚硬的外壳、对潮汐完全的掌握、以及震慑人心的锚击，使他成为当之无愧的强大对手。没有人知道这个怪物到底是怎么诞生的，但他肆无忌惮而又无可匹敌的力量已经使他成为了传说。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (34, '半人猛犸', '', 'Magnus', 'Mag', 'mag.gif', 'display_mag.jpg', '', '天灾', '团控', '中等', '獠牙冲刺尽量命中 大招无视魔免', 549, 221, 49, 61, 128, 1.70, 0.0, 0.0, 315, 4, 'STR', 21, 15, 17, 2.8, 2.5, 1.6, 3, 4, 6, 4, 4, '也许是天灾军团中最巨型生物的缘故，Magnus的力量让人难以招架。他从出生以来就一直为了成为强力的战士而磨炼着。而现在，他已到达了他力量的颠峰。他的巨斧不但能从他的敌人中劈开血路，而且还能发出碎骨冲击，让大地产生波纹。更厉害的是，他的攻击可以通过恶魔魔力的强化，从而轻而易举地粉碎敌人。尽管如此，他真正的实力还是在于他能够产生一个宽阔而且强力的磁场，将那些软弱的敌人吸到一块儿，然后去面对他们生命的尽头。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (35, '裂魂人', '', 'Barathrum', 'SB', 'sb.gif', 'display_sb.jpg', '', '天灾', 'gank', '易', '大招躲技能 利用冲刺逃生 配合疯脸BKB克制站桩后期', 587, 182, 54, 64, 128, 1.70, 0.0, 0.0, 295, 5, 'STR', 19, 17, 15, 2.4, 1.7, 1.8, 7, 3, 8, 2, 4, '自从被灵蹄部落放逐以后，Barathrum带着羞愧藏身于灵界。在那里，他以吞食游魂为生，就这样浑浑噩噩、孤独而又凄惨地游荡了好几个世纪。在被巫妖王发现并招至帐下以后，Barathrum不可思议的速度和幽魂般的力量得以在尘世释放。他的灯笼能向对手投射冥界的力量，将他们向后推送。藉着瞬间靠近对手的能力，Barathrum对于任何人来说都是不容小看的对手。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (36, '沙王', '', 'Crixalis', 'SK', 'sk.gif', 'display_sk.jpg', '', '天灾', 'GANK', '难', '大招shift跳刀跳大 穿刺后最速摇大 沙尘暴与穿刺躲技能 法球压制近战', 492, 208, 43, 59, 128, 1.70, 0.0, 0.0, 300, 3, 'STR', 18, 19, 16, 2.6, 2.1, 1.8, 2, 7, 7, 4, 3, 'Crixalis曾经是Kalimdor的沙漠中的一只普通的蝎子。因为某种原因，他比其他弟兄发育得更快，成长得更大。由于他更容易被巫妖王的精神控制所影响，巫妖王将他召唤过来守护冰封王座。虽然比以前更大了，但是Crixalis仍然保持着他作为蝎子时的能力，比如钻入地下，然后在别的地方钻出，并将他经过的所有敌人刺到空中。他的尾刺含有一个剧毒的毒腺，它分泌的毒素能使他的敌人爆裂而死。斥候们发现，他甚至能引发强烈的地震，并杀死在他附近移动的所有敌人。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (37, '敌法师', '', 'Magina', 'AM', 'am.gif', 'display_am.jpg', '', '近卫', '后期', '中等', '分身继承消蓝法球 大招V有打断和AOE效果', 530, 195, 49, 53, 128, 1.45, 0.0, 0.0, 315, 3, 'AGI', 20, 22, 15, 1.2, 2.8, 1.8, 8, 6, 4, 2, 6, '一种与生俱来的天赋，Magina甚至可以看见魔法能量以最纯粹的形式出现并控制它。天灾军团对世界之树的污染破坏了这个世界的魔法结构。而Magina将不惜牺牲自己来保持大自然的平衡。 一种与生俱来的天赋，Magina甚至可以看见魔法能量以最纯粹的形式出现并控制它。天灾军团对世界之树的污染破坏了这个世界的魔法结构。而Magina将不惜牺牲自己来保持大自然的平衡。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (38, '矮人狙击手', '', 'Kardel Sharpeye', 'sniper', 'sniper.gif', 'display_sniper.jpg', '', '近卫', '后期', '中等', '散弹对建筑物有伤害 可以无伤拆塔和泉水', 454, 195, 36, 42, 550, 1.70, 0.0, 0.0, 290, 2, 'AGI', 16, 21, 15, 1.7, 2.9, 2.6, 6, 3, 5, 2, 2, 'Kardel是近卫军团的狙击专家。由于他超远的射程，总能在很安全的位置给敌人造成十分巨大的伤害。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (39, '主宰', '', 'Yurnero', 'Jugg', 'jugg.gif', 'display_jugg.jpg', '', '近卫', '后期', '中等', '剑圣风暴魔免躲技能 野外1v1单杀强力 治疗守卫用来死磕和推进 大招附带普攻效果', 530, 182, 44, 48, 128, 1.60, 0.0, 0.0, 305, 4, 'AGI', 20, 20, 14, 1.9, 2.8, 1.4, 6, 6, 7, 5, 4, '他出剑似舞蹈般灵动轻盈，无论多坚固的护甲都已悄然破碎：他行动如诗歌般优雅从容，无论多强的敌人弹指间灰飞烟灭。是的，Yurnero追求完美，追求人剑合一的至高境界。成为那无人能敌的剑圣便是他的宿命。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (40, '德鲁伊', '', 'Syllabear', 'Ld', 'ld.gif', 'display_ld.jpg', '', '近卫', '后期,推进', '难', '双线操作 小熊撑输出，本体撑肉 小熊被英雄攻击时无法召回 小熊可以吃F 小熊缠绕无视魔免', 473, 169, 46, 50, 550, 1.70, 0.0, 0.0, 315, 3, 'AGI', 17, 24, 13, 2.1, 2.7, 1.4, 7, 8, 4, 2, 7, '在那次剧烈的冲突中，德鲁伊是他的部族中仅存的一个。当感觉到居住的地方面临着毁灭的时候，他的族人们将他们的孩子变成一头熊，并放至山林。德鲁伊渐渐成长，并且变得和森林里那些栖息在他家的熊一样强壮，凶猛。他能通过德鲁依的力量进入一个狂热的状态，甚至能呼唤强大的熊伙伴来援助他。被先知法里奥唤醒他的精灵血统后，德鲁伊重新变回精灵形态，并将他的野性灵魂带到战场中去。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (41, '月之骑士', '', 'Luna Moonfang', 'Luna', 'luna.gif', 'display_luna.jpg', '', '近卫', '后期', '中等', '白天和晚上视野一样 野外1V1单杀强力', 435, 208, 43, 49, 330, 1.70, 0.0, 0.0, 330, 3, 'AGI', 15, 22, 16, 1.9, 2.8, 1.8, 8, 7, 5, 2, 3, 'Luna是月神坚定而又虔诚的信徒。在无尽的战争中，她加入近卫军团竭尽全力净化被邪恶的天灾军团所污染的土地。由于她的忠诚和勇敢，月神赐予了她一小部分神秘的力量。这份恩赐使得她能轻易打败敌人。据说Luna能够召唤最纯净的月光，而且永远被灼炽的光环所围绕，就好像身处在月光的照耀下。Luna是近卫军团最明亮的灯塔和永不懈怠的守卫。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (42, '变体精灵', '', 'Morphling', 'Morph', 'morph.gif', 'display_morph.jpg', '', '近卫', '后期', '难', '波浪期间无敌 转属性保命 无法复制自己的幻象 团战时复制强大的队友或敌人', 511, 221, 32, 41, 350, 1.70, 0.0, 0.0, 285, 1, 'AGI', 19, 19, 17, 2.0, 3.0, 1.5, 8, 6, 4, 2, 6, '变体精灵，大自然的杰作，进化的巅峰，能将他液态的身体化作巨大的波浪，一边移动一边对流经的敌人造成伤害。他可以改变自身的组成，使自己变得更有力又或是更灵活。通过调整力量和敏捷的平衡，他可以选择震晕又或是伤害敌人。而真正让他如此非比寻常的地方在于他可以克隆一个选定的单位，在敌人不得不面对自身力量而惊慌失措时碾碎他们。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (43, '娜迦海妖', '', 'Slithice', 'Naga', 'naga.gif', 'display_naga.jpg', '', '近卫', '后期', '中等', '开大拆塔 分身躲技能 诱捕无视魔免且有打断效果 大招追杀或逃跑', 549, 234, 44, 46, 128, 1.70, 0.0, 0.0, 320, 6, 'AGI', 21, 21, 18, 2.3, 2.8, 2.0, 8, 6, 6, 2, 4, 'Slithice喜欢跨出大海的边界，找寻新的事物，作为娜迦族的一员，她永远过着流浪的生活。在旅途中她遇见了一个想成为剑圣的年轻半兽人并同他共坠爱河。即使年轻的半兽人自己也没有什么实战经验，他还是教授了Slithice许多半兽人故老相传的战斗技巧。在他们一起度过的甜蜜时光中，她还从当地的骑兵那里学会了如何设置陷阱。然而一切的幸福都随着天灾军团的到来化为泡影，她的爱人在她面前被杀害，她使用娜迦族的沉睡魔法得以逃脱。现在，她把她的一切投入到对抗天灾军团的战斗中，为了她心中永不会磨灭的爱！');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (44, '幻影长矛手', '', 'Azwraith', 'lancer', 'lancer.gif', 'display_lancer.jpg', '', '近卫', '后期', '中等', '隐身躲技能 辉耀幻象磨血 幻象刷野怪', 492, 273, 45, 67, 128, 1.70, 0.0, 0.0, 290, 3, 'AGI', 18, 23, 21, 1.7, 4.2, 2.0, 7, 7, 4, 2, 5, '尽管盟友中没有人知道他的真名，可是幻影长矛手这个英雄称号已经告诉你足够多了。就像死亡天使亚兹拉尔一样，他为清除掉世界上那些皈依亡灵的人而战。他像幽灵一样按照自己的意愿神出鬼没，能够同时出现在许多地方，没有人比他更擅长使用长矛来瓦解亡灵部众了，每次打击都使敌人离地狱更进一步。他的长矛能够赋予他惊人的能力，复制自身的镜像，每当他的长矛吸入鲜血时，他就用敌人的生命力制造出一个自身的稍弱镜像。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (45, '月之女祭司', '', 'Mirana Nightshade', 'Pom', 'pom.gif', 'display_pom.jpg', '', '近卫', 'gank', '难', '跳跃躲技能 预判月神箭 释放大招打突袭或逃跑', 473, 221, 38, 49, 600, 1.70, 0.0, 0.0, 300, 2, 'AGI', 17, 20, 17, 1.8, 2.8, 1.6, 7, 5, 8, 4, 2, '作为月神Elune庇护下的长老和高等祭司，Mirana Nightshade在近卫军团中就如同那黑暗中的一线光明。她以雨点一般的箭矢和坠落的流星抵挡蜂拥而至的天灾大军，而她神圣的存在，消除了四周友军的疲倦，让他们在战场上的速度得到极大的提升。在必要的时候，她还能以隐形掩护自己和其他伙伴，使她成为重要的支援力量。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (46, '隐形刺客', '', 'Rikimaru', 'Sa', 'sa.gif', 'display_sa.jpg', '', '近卫', '后期', '中等', '大招永久隐身 雾群体减速沉默 带散失单杀落单英雄', 473, 182, 48, 52, 128, 1.70, 0.0, 0.0, 300, 6, 'AGI', 17, 34, 14, 2.0, 2.9, 1.3, 8, 2, 8, 2, 4, '作为塞特斯一族的继承人，Rikimaru接受了族内最强勇士的严格训练。然而，燃烧军团的力量腐蚀了他许多族人的心智，使他们成为森林中的行尸走肉。他苦练技艺，发誓要向天灾军团复仇。他运用身材小巧的优势隐匿自己的行踪，从背后发动突袭。通过烟雾的掩护，敌人根本连他的衣角都沾不到。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (47, '巨魔战将', '', 'Jah`rakal', 'Troll', 'troll.gif', 'display_troll.jpg', '', '近卫', '后期', '中等', '大招全地图有效 切换形态放飞斧 切换形态可以躲技能', 473, 169, 38, 56, 500, 1.70, 0.0, 0.0, 300, 2, 'AGI', 17, 21, 13, 2.2, 2.8, 1.0, 8, 4, 4, 2, 3, '在近卫军团第一次集结时，那些巨魔战士们曾经是被大家认为是不开化并且很不可靠的。他们的自尊心因此受到伤害，很多人拒绝加入，有些甚至考虑加入天灾军团。只有一个叫做Jak`Rakal的年轻热血的巨魔战将加入了近卫军团。他之所以被巨魔们称做“眩目之斧”，是因为他挥舞斧头的速度令人眩目，并且具有让敌人失明的招牌技能。他那不可阻挡的狂暴力量早已在他的盟友中成为一个传说。但是，Jah`Rakal不是为了近卫军团而战斗，甚至不是为了守护世界之树，而仅仅是为了向精灵，人类，兽族以及其他一切种族证明，巨魔一族才是世界上最强的战士。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (48, '矮人直升机', '', 'Aurel Vlaicu', 'AV', 'av.gif', 'display_av.jpg', '', '近卫', 'GANK', '中等', '导弹跟踪GANK A杖全地图大招 有一定后期能力', 492, 299, 41, 51, 375, 1.70, 0.0, 0.0, 315, 2, 'AGI', 18, 24, 23, 1.8, 2.8, 2.1, 6, 4, 7, 5, 2, '奥雷尔，这个出生于采矿之家的矮人，对爆破有着与生俱来的兴趣与天赋。这种超乎常人的天赋在矮人矿工社会中确定了他极高的收入、头衔和声望。但是奥雷尔并不像族人那样安份于爆破矿石，他运用自己的技能组合可控炸药与厚重金属，从发动机到飞弹，任何东西都可以轻易地制造出来。他最热衷的事情莫过于将他的发明带到战场上测试，从而使战场变得极度炫目。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (49, '卓尔游侠', '', 'Traxex', 'DR', 'dr.gif', 'display_dr.jpg', '', '近卫', '后期', '中等', '对线法球压制 手动法球追击', 473, 195, 40, 51, 625, 1.70, 0.0, 0.0, 300, 2, 'AGI', 17, 22, 15, 1.9, 1.9, 1.4, 8, 3, 4, 2, 2, '卓尔游侠曾是地底世界幽暗地狱的一名优秀的弓箭手。她对她的家族所走的邪路厌恶至极，便逃到了地表世界。她的坚毅，使她带着她那杰出的实战弓术加入了近卫军团。她的技能包括剥夺魔法生物的嗓音，以及为她的箭矢附上寒冰魔法，还有她那每箭力求精准的成名技。她这些能力非常有价值，所以在其他人眼里，她的真实身世也就无足轻重了。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (50, '圣堂刺客', '', 'Lunaya', 'Ta', 'ta.gif', 'display_ta.jpg', '', '近卫', 'GANK', '难', '折光隐匿躲技能 灵能之刃压制 新手勿用', 492, 260, 53, 59, 140, 1.70, 0.0, 0.0, 305, 4, 'AGI', 18, 23, 20, 2.1, 2.7, 2.0, 5, 2, 7, 4, 4, 'Lunaya师从于一位来自神族母星Aiur的传奇人物。她是一名使用精神力量的高手，并且在近卫军团中专门负责进行战略性质的刺杀行动。她的心灵异能，能够让她随意的进入和脱离战斗，并且可以增强攻击时所造成的伤害，同时保护她不受敌人的袭击。或者在狩猎哪个不走运的路人之前给自己披上一件隐身衣。敌人在战场上发现这个圣堂武士时，往往也就是他们死亡并被埋葬之时，当他们的生命被终结后，她便再次潜入暗影，等待下一个猎物。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (51, '熊战士', '', 'Ulfsaar', 'Ursa', 'ursa.gif', 'display_ursa.jpg', '', '近卫', 'GANK', '中等', 'V快结束时出手12连拍 带祭品单挑肉山', 587, 208, 45, 49, 128, 1.70, 0.0, 0.0, 310, 5, 'AGI', 23, 18, 16, 2.9, 2.1, 1.5, 7, 3, 4, 2, 4, '在天灾军团入侵以前，一群被称为毛皮兽的智慧熊族在Azshara的心脏地带建立了一个安宁而具灵性的社会。在大部分成员被不死族杀害或腐化以后，原本与世隔绝的熊族不得不向近卫军团寻求庇护。作为回报，熊族派出了他们最强大的熊战士参加到近卫军团。出于对玷污了他们圣地的敌人的愤怒，Ulfsaar将他的怒火转化为一连串快速而残忍的攻击。他用他剃刀般的爪子击败敌人，将他们撕裂。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (52, '复仇之魂', '', 'Shandelzare Silkwood', 'VS', 'vs.gif', 'display_vs.jpg', '', '近卫', 'GANK,辅助', '易', '吼叫减甲开视野 大招先手换对面核心 大招救队友', 454, 195, 39, 53, 400, 1.70, 0.0, 0.0, 295, 4, 'AGI', 16, 27, 15, 2.3, 2.8, 1.8, 4, 4, 8, 9, 4, '复仇之魂曾经是一位威严的典狱官，妹妹幻影刺客的狞笑以及滴血的圆环月刃的影像至死萦绕在她的心头。月神艾露恩听到她忠诚女儿的哭泣后决定拯救她，并给予她可以使用冥界力量的新的躯体，以及亲临死亡的人才拥有的斗志。饱含着前生可怕的热情的复仇之魂，现在率领着近卫军团，无所畏惧迎接未来的挑战。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (53, '赏金猎人', '', 'Gondar', 'Bh', 'bh.gif', 'display_bh.jpg', '', '近卫', '后期', '中等', '大招提供视野与加速 团战全员给大招 发团战财 疾风步单劣势路', 473, 247, 45, 59, 128, 1.70, 0.0, 0.0, 315, 6, 'AGI', 17, 21, 19, 1.8, 3.0, 1.4, 5, 2, 7, 3, 2, '说到精湛的技艺，人们经常会想起Gondar这个名字。关于他的过去人们所知甚少，不过他的能力的确非比寻常。有人说他师从一个忍者组织，也有人说他的能力完全来自他的天赋。他的飞镖非常的精准，他锋利的双刀击倒任何敌人，他的敏捷无人能比，他隐形能力也数一数二。他只对赏金感兴趣，并已被近卫军团许以重金。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (54, '灰烬之灵', '', 'Ember Spirit', 'Em', 'em.gif', 'display_em.jpg', '', '近卫', 'GANK', '难', '利用大招补给回线与换线 预先释放大招逃生与切入 枷锁避免小兵干扰 无影拳躲技能', 549, 260, 52, 56, 128, 1.70, 0.0, 0.0, 310, 1, 'AGI', 19, 22, 20, 2.0, 1.8, 1.8, 7, 5, 8, 3, 6, '在战争铸剑熔炉中，一个强大的事物逐渐成型。为了锻造一把超级武器，一个名为XXX的铸剑师铤而走险捕获了一个火精灵，并将其投入自己的熔炉。经过无数小时的辛苦工作，一对利剑从魔力熔炉中诞生了。每当握着这把剑仔细欣赏时，火精灵都穿入他的身体，造就了融合着一个肉体与火焰的生物。从此愈发强大，一次又一次地将挡路的敌人的热能和生命一起剥夺。拥有闪电的速度和灼热的利刃，毫不费力地将他的敌人撕裂，只留下一地尘埃。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (55, '血魔', '', 'Strygwyr', 'BS', 'bs.gif', 'display_bs.jpg', '', '天灾', 'gank', '中', '靠补刀杀人回血 刃甲是核心，推推是误区 血之狂暴强单体沉默', 587, 234, 53, 59, 128, 1.70, 0.0, 0.0, 305, 3, 'AGI', 23, 24, 18, 2.0, 3.0, 1.7, 6, 3, 6, 3, 4, '作为恶魔和兽人的混血儿，Strygwyr被所有凡间的生物所排斥。Strygwyr用他所杀的生物的血来沐浴，并能以此来治疗他的伤口-虽然这证明了他的不幸。那些冒险的傻瓜们必须得知道的是，Strygwyr可以在几里外感受到他们血液的流动，并随后获取他们无助的视野，当Strygwyr的嗜血狂怒将敌人撕裂时，他的猎物面对一道无解的选择题：尝试着与这位快速的恶魔竞速时流血而亡，或者呆在原地被他狂暴的撕成碎片。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (56, '骷髅射手', '', 'Clinkz', 'Bone', 'bone.gif', 'display_bone.jpg', '', '天灾', '后期', '中', '大招吃野隐身GANK 线上法球步压制', 435, 208, 37, 43, 600, 1.70, 0.0, 0.0, 300, 2, 'AGI', 15, 22, 16, 1.6, 3.0, 1.6, 7, 5, 7, 2, 4, '这位堕落的精灵弓箭手脸上永远不灭的火焰，向旁人证明他是一个纯粹的亡灵。Clinkz追求箭艺的完美，并且为了达到目的，从不介意牺牲他的盟友。他随风而动，并时刻准备着用他那迅捷并且准确的箭术来刺杀敌军的军官。他的箭能让对方感觉到来自冥界的热度，并将危险慢慢的带到他们的身旁，生前是精灵射手，死后是敌人的噩梦。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (57, '育母蜘蛛', '', 'Black Arachnia', 'brood', 'brood.gif', 'display_brood.jpg', '', '天灾', '推进', '中等', '魂戒无限吐蜘蛛 蛛网破树强杀 小蜘蛛围杀', 473, 234, 44, 50, 128, 1.70, 0.0, 0.0, 295, 3, 'AGI', 17, 18, 18, 2.5, 2.2, 2.0, 7, 8, 5, 2, 4, '她曾经是Nerubian一族的女王，以统治严明而著称。然而和变节的刺客Anub`Arak的相爱无疑是她犯的一个致命的错误，她也因此受到了王国的惩罚。现在她和他们的后代----作为她不死的爱恋的象征----投入到这场世纪之战中。她是织网的高手，她的能力能使任何敢于攻击她的对手麻痹无力，她还会将卵产入这些可怜的牺牲品的体内，一旦他们死亡，那些幼虫就会破体而出。她对于战斗的渴望，支撑着她并使她变得更为强大。这使许多人相信，她对于血肉的欲望是永远不会满足的……');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (58, '地穴刺客', '', 'Anub`arak', 'NA', 'na.gif', 'display_na.jpg', '', '天灾', 'gank', '难', '大招起手接穿刺抽蓝 前期GANK为主 外壳躲技能', 492, 234, 49, 53, 128, 1.70, 0.0, 0.0, 300, 4, 'AGI', 18, 19, 18, 2.0, 2.2, 2.1, 4, 4, 7, 2, 5, 'Anub`arak曾经是地穴蜘蛛人中的佼佼者，但在第二次蜘蛛战争的时候不幸殒命。巫妖王耐奥祖复活了他，并承诺给予他永恒的生命。但作为交换条件，他必须始终不渝地效忠巫妖王。他的尖刺外壳坚硬无比，能将敌人的部分攻击反弹回去。当感到有危险的时候，他会向敌人刺出这些尖刺，使他们晕头转向。他不但能用他的暗黑力量燃烧敌人的魔力，而且能将自己融入到冰冻苔原的阴影中去。而那些看过他融入暗影的人，却从来没有一个能活着将这件事情传扬出去。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (59, '地穴编织者', '', 'Anub`seran', 'NW', 'nw.gif', 'display_nw.jpg', '', '天灾', '后期', '难', '线上二连击压制 蝗虫群开视野断兵 缩地后522极速 大招解粉解毒保命', 435, 195, 46, 56, 425, 1.70, 0.0, 0.0, 290, 1, 'AGI', 15, 14, 15, 1.5, 2.5, 1.8, 7, 4, 5, 3, 4, 'Anub`seran，这个生物的名字使那些见过地穴蜘蛛人的人们深深地感到恐惧。这头野兽来自于一个纯意识的平行空间，即使是久负盛名的地穴刺客----Anub`arak，在面对他的时候也会犹豫不决。他会迅速穿过敌人，玩弄敌人于股掌之中，并且在他跨过敌人的身体的时候伤害他们。他能运用跨越空间的能力进行短暂的时间旅行，让自己回到过去，以使他远离死亡。他甚至可以从未来借用一次攻击去打击他的敌人，让他的突击更为致命。Anub`seran，只有傻子才不懂得害怕这个名字。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (60, '幻影刺客', '', 'Mortred', 'PA', 'pa.gif', 'display_pa.jpg', '', '天灾', '后期', '中等', '模糊反GANK 对线用D辅助补刀 4倍暴击 DB短CD施法远，克制闪烁', 530, 169, 46, 48, 128, 1.70, 0.0, 0.0, 310, 4, 'AGI', 20, 23, 13, 1.8, 3.1, 1.0, 7, 3, 5, 2, 4, '出生于暗夜精灵的典狱长一族，由于受到暗夜主流社会的疏远，为了报复，Mortred发誓效忠天灾军团。她是力量和速度的完美结合，通过长年在阿森维尔森林的独处，学会了将身形融于周围的环境之中，每每在你最意想不到的时刻如幻影般出现。她精准而致命的攻击使她成为天灾军团极为重要的一员。这也就是为什么她被称为幻影刺客（Phantom Assassin）。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (61, '影魔', '', 'Nevermore', 'SF', 'sf.gif', 'display_sf.jpg', '', '天灾', 'gank', '难', '杀死单位积攒灵魂 黑皇跳大 影压三连压与G点 取消影压施法后摇 跳压与转身压', 435, 234, 35, 41, 500, 1.70, 0.0, 0.0, 300, 2, 'AGI', 15, 20, 18, 2.0, 2.4, 2.0, 6, 7, 7, 2, 3, 'Nevermore是一种能吸收身体周围的灵魂的生物。他虽然是来自燃烧军团的魔鬼，但是却讨厌和同类的魔鬼一样蠕动，于是便重新伪装了自己。当影魔投入到战斗的时候，便能通过吸收灵魂来增强自己的力量，还能用他被灵魂增强过的攻击以及强力的精神冲击去击败对手。在遭遇过他的少数幸存者的心目中，影魔是最恐怖的对手，而那些被他残暴地杀死的人，才算见识过什么是世界上最卑鄙的杀人手段。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (62, '灵魂守卫', '', 'Terrorblade', 'Tb', 'tb.gif', 'display_tb.jpg', '', '天灾', '后期', '难', '倒影克制成型后期 大招换血无视魔免 变身加分身斧五恶魔', 435, 247, 48, 54, 128, 1.50, 0.0, 0.0, 310, 7, 'AGI', 15, 22, 19, 1.9, 3.2, 1.8, 8, 6, 2, 1, 4, 'Terrorblade和Magina原是孪生兄弟。虽然同样流着暗夜精灵的血，Terrorblade却受到不死族力量的诱惑而陷入不归的深渊。背上巨大的恶魔之翼代表了他和暗夜世界的彻底决裂。他熟练的掌握了镜像的制造以及对灵魂的操纵。他可以变身为可怕而强大的恶魔，通过投掷能量打击对手。挥舞着巨大月刃的他无疑是战场上的一个噩梦。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (63, '幽鬼', '', 'Mercurial', 'SPE', 'spe.gif', 'display_spe.jpg', '', '天灾', '后期', '中', '大招收人头混助攻 反弹伤害能力强 幽鬼之刃中移动无视地形', 511, 208, 46, 50, 128, 1.70, 0.0, 0.0, 295, 3, 'AGI', 19, 23, 16, 2.0, 2.2, 1.9, 8, 3, 6, 1, 8, '诞生于远古世界早期的黑暗，Mercurial代表着那些死去多时的灵魂的狂怒和怨恨。和死灵与恶灵一样，它神出鬼没，并以不寻常的热情纠缠那些还活着的生命，而且完全不受自然法则的约束。它还可以利用它垂死友军的生命能量去摧毁天灾军团的敌人。一切都为了冰封王座的荣耀。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (64, '剧毒术士', '剧毒', 'Veon', 'Veon', 'veon.gif', 'display_veon.jpg', '', '天灾', '推进', '简单', '猛插蛇棒推进 要确保团战放出大招', 492, 195, 46, 48, 450, 1.70, 0.0, 0.0, 290, 3, 'AGI', 18, 22, 15, 1.8, 2.6, 1.8, 3, 4, 5, 4, 3, '对那些沉醉于死亡的人来说，下毒是必修的一课。只是，Lesale做的过火了一些。他甚至在自己身上试验各种毒药和化学物质以获得死亡的秘密。这些毒物扭曲了他的灵魂，畸化了他的身体，使他成为了对一切纯净事物抱有深刻仇恨的丑恶生物。他可以使用浓缩的毒药攻击敌人，他甚至可以放出充满整个空间的毒气，严重的削弱周围任何的生物——不管是人是鬼。 ');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (65, '冥界亚龙', '', 'Viper', 'Viper', 'viper.gif', 'display_viper.jpg', '', '天灾', 'gank', '中', '线上法球压制 出肉装当肉盾 利用粘人能力多GANK', 473, 195, 44, 46, 575, 1.70, 0.0, 0.0, 285, 2, 'AGI', 17, 21, 15, 1.9, 2.5, 1.8, 4, 2, 8, 2, 7, 'Viper是一头凶猛的，喷吐着酸液的野兽。身为一条被巫妖王亲手驯服的恶毒的亚龙，他的速度和敏捷能让绝大多数坚强的战士吃惊不已。Viper能通过自我诅咒到达一个疯狂的愤怒状态，不顾死活地在战斗中横冲直撞。他的唾液腺分泌出的刺人的毒素，能使敌人痛苦地翻滚，强烈地伤害他们的神经。当你面对这个强力的怪兽时一定要多加小心……他黑暗的身影可能会成为你所能看到的最后一个东西。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (66, '地卜师', '', 'Meepo', 'Meepo', 'meepo.gif', 'display_meepo.jpg', '', '天灾', '推进', '难', '多线操作 跳刀忽悠 四狗齐飞 多线刷钱', 587, 260, 39, 45, 128, 1.70, 0.0, 0.0, 305, 5, 'AGI', 23, 23, 20, 1.6, 1.9, 1.6, 7, 5, 7, 2, 3, '生活在黑森林中，最初被赫尔马尼的异族所崇拜的神Meepo，是个淘气的小土灵。他特别喜欢将他的敌人活生生地困在岩石的裂缝中，然后用他沉重的铲子将他们钉死在绝望之中。地卜师最让敌人头大的本事，还是能够分出另外的自己，并且每个分身都跟原本的真身一样。所以谁不幸地惹到了他，就可能遇到4倍于平时的麻烦。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (67, '闪电幽魂', '', 'Razor', 'Razor', 'razor.gif', 'display_razor.jpg', '', '天灾', 'gank', '易', '静电链接抽攻击力 不稳定电流净化对方', 549, 247, 45, 47, 475, 1.70, 0.0, 0.0, 295, 2, 'AGI', 21, 22, 19, 1.7, 2.0, 1.8, 6, 5, 6, 2, 5, 'Razor是闪电的化身。它疯狂地向自己周围的物体释放着无穷的电能。就这点而言，它勉强能被称做“生物”。能判断出它有感觉的唯一依据，就是它能够精准地控制它的电流不去伤害那些它称做朋友的人。和它的名字----剃刀----的含义一样，它的攻击能够快速并精准地切断敌人的骨头，甚至有时可以延续到下一个敌人。非自然的生命形式给予了它惊人的速度，以及对鲜血的渴望。它非常难对付，只有无所畏惧的强者才敢面对它。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (68, '鱼人夜行者', '', 'nightcrawler', 'NC', 'nc.gif', 'display_nc.jpg', '', '天灾', '后期', '中等', '开大躲技能安全输出 黑暗契约破控制解状态', 549, 208, 48, 56, 128, 1.70, 0.0, 0.0, 305, 1, 'AGI', 21, 15, 16, 1.8, 1.5, 1.9, 4, 5, 8, 4, 2, '在漆黑的海洋深处，潜伏着令人恐惧的生物。他可以随意的操控阴影，很少有人见过他的原型，更从没有人活到了可以提起他的时候。阴影是他的避难所，一个令他更为强大和能够重生的地方。在无法穿透的黑暗里，他的攻击没有人可以看见。每一次进攻都使他更为强大和迅速，与此同时削弱着他的猎物。一条坚不可摧的皮带等待着所有妄想逃跑的猎物，让他们任他那可怕的幻影摆布。他牺牲了自己灵魂的一部分来与黑暗签订了可以摧毁最强大的束缚和诅咒的契约。当大战的回音到达了世界上的所有角落，这个被称为slark的生物，从海洋深处走了出来。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (69, '虚空假面', '', 'Darkterror', 'Fv', 'fv.gif', 'display_fv.jpg', '', '天灾', '后期', '中等', '利用大招团控或逃生 敲晕无视魔免', 587, 195, 58, 64, 128, 1.70, 0.0, 0.0, 300, 4, 'AGI', 23, 21, 15, 1.6, 2.6, 1.5, 6, 2, 7, 2, 4, '据说他曾经是人类的一员，只是他的过去已经被深沉的黑暗吞没，甚至他自己都已经无法回忆。我们只知道他曾被抛入时间的缝隙，在他回来的时候，他已经掌握了操纵时间的能力。他能够锁定敌人的时间，他也能通过短暂地回溯来躲避攻击。他甚至可以将身边的时空结构撕裂，处于其中的----不论敌我----都无法动弹，当然除他以外。传言他可以瞬间对四周的任意一个敌人发动攻击，却没人真正看到他靠近……');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (70, '蛇发女妖', '蛇发', 'Medusa', 'Medusa', 'medusa.gif', 'display_medusa.jpg', '', '天灾', '后期', '难', '开盾吸收伤害 大招对召唤生物伤害高 节奏慢需要大后期发威', 473, 247, 44, 50, 600, 1.70, 0.0, 0.0, 290, 2, 'AGI', 14, 20, 19, 1.6, 2.5, 1.8, 7, 6, 2, 1, 9, '蛇发女妖是被永世囚禁在大漩涡深处纳兹加塔海底监狱的阴险生物，在阿尔萨斯所率领的天灾军团和诺森德冰雪海岸的娜迦所掀起的战争期间无意被释放。她被亡灵感染，结果却得到了不朽的庇护。凭借着精湛的箭术和对魔法与生俱来的亲和力成名多年，她一挥手就能射出多支箭矢，使用秘术异蛇削弱敌人，甚至能用魔法形成屏障保护自己。如同传说中的美杜莎一样，任何面对她的敌人都不免被恐惧所占领渐渐麻痹。 ');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (71, '水晶室女', '', 'Rylai Crestfall', 'Cm', 'cm.gif', 'display_cm.jpg', '', '近卫', '辅助', '易', 'E对非英雄单位作用10秒 V后H&R压制 对面打断技CD时放大', 454, 273, 38, 44, 600, 1.70, 0.0, 0.0, 280, 1, 'INT', 16, 16, 21, 1.7, 1.6, 2.9, 2, 5, 7, 9, 1, '熟练掌握冰霜魔法的水晶室女是智慧与优雅的化身。深入冬泉之地的腹地，师从于一名叛变法师的水晶室女带给暗夜哨兵军团她高超的魔法技艺。有着庞大的魔法储备的水晶室女，或者是用强力的霜爆魔法让他们恐惧中逃跑，或者是使得他们被冰所困不能移动。她所释放出的巨大霜冻新星阵列，使所有敢面对她的敌人巍巍颤抖，并将他们一并抹去。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (72, '魅惑魔女', '', 'Aiushtha', 'AS', 'as.gif', 'display_as.jpg', '', '近卫', '推进', '难', '带小弟多线操作 普攻出手后拉距离增加伤害 害怕高爆发法系输出', 454, 208, 47, 57, 550, 1.70, 0.0, 0.0, 305, 2, 'INT', 16, 19, 16, 1.0, 1.8, 2.8, 6, 4, 8, 5, 8, '虽然所有森林女神都是赛那留斯的女儿，但Aiushtha却是赛那留斯的第一个女儿，也是他最疼爱的一个。因为这层关系，Aiushtha能从赛那留斯那里借取更多的力量。之所以这么说，一是因为她能控制那些害羞的小精灵去医治自己的盟军，二是因为她能够让一个敌人服从近卫军团的意愿。相对于肉搏来说，她更喜欢射击，所以她亲手打造了一把长矛，能对越远的敌人造成越大的伤害。她不可侵犯，那些企图杀死她的敌人将会发现她会非常轻松地从他们的面前跳开，真的非常轻松。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (73, '仙女龙', '', 'Puck', 'Puck', 'puck.gif', 'display_puck.jpg', '', '近卫', 'GANK', '难', '手动F躲大部分技能 灵动之翼也可以躲技能 RWC的技能间隙多用普攻 避免无把握时一次性用光所有技能', 435, 325, 42, 53, 550, 1.70, 0.0, 0.0, 295, 2, 'INT', 15, 22, 25, 1.7, 1.7, 2.4, 2, 6, 8, 4, 2, '近卫军团一直在寻找能够帮助他们对抗天灾的英雄，最终他们发现了生活在失落大陆上的神秘的仙女龙。在解释清楚同女王阿格瑞斯的冲突后，她决定派他的贴身侍卫PUCK来扭转整个战争的局势。虽然PUCK身材纤细又调皮捣蛋，不过在战场上却有着足以证明自己的表现，能穿过所有敌人爆炸性魔法球，仙女粉尘，已经以无以伦比的幻术麻痹所有敌人的能力，给所有和她对抗的敌人上了一课，所谓不可以貌取人，在他们面前出现的，是无穷无尽的欺骗。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (74, '圣骑士', '', 'Chen', 'Chen', 'chen.gif', 'display_chen.jpg', '', '近卫', '辅助,推进', '难', '带宝宝多线操作 TAB切换与编队切换 用shift实现精细操作 大招可以用来救小弟 用R救危险队友 用R反控对面召唤物', 530, 273, 43, 53, 600, 1.70, 0.0, 0.0, 300, 1, 'INT', 20, 15, 21, 1.5, 2.1, 2.8, 3, 7, 6, 8, 3, '为了预言中的新的黎明，一个没有恶魔的纯净世界，圣骑士加入到对抗天灾的战争之中，改变着历史的进程，作为Hextar旗下的一名远征军，他给予那些跟随他以战斗的人们以永久的光辉，允诺赦免他们的罪过，也随时考验着那些宣誓净化恶魔的人的忠诚，他的追随者不管来自哪个种族，有什么样的背景，都会狂热的投入到他的事业中去，在冰峰王座摧毁之前绝不罢休，如果上帝真的有向大地伸出援手的话，那么这只手一定是圣骑士，CHEN！');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (75, '光之守卫', '', 'Ezalor', 'Kotl', 'kotl.gif', 'display_kotl.jpg', '', '近卫', '辅助,推进', '易', '找阴影施放冲击波 查克拉加魔 致盲之光克制物理DPS 利用传送GANK推进', 454, 286, 38, 54, 600, 1.70, 0.0, 0.0, 315, 1, 'INT', 16, 15, 22, 1.8, 1.6, 2.8, 2, 6, 5, 9, 1, 'Ezalor由于在魔法师战争中扮演了叛徒的角色而被放逐到灵界。近卫军团以他提供一个肉体为代价获得了他的忠诚。通过使用许多让人印象深刻的辅助性法术，Ezalor以并不光彩的身份参与到近卫军团的战斗中。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (76, '众神之王', '', 'Zeus', 'Zeus', 'zeus.gif', 'display_zeus.jpg', '', '近卫', 'GANK', '中等', '用C触发静电场磨血 团战先放大 静电场有反隐效果 血量计算最重要 G打断持续施法', 511, 260, 41, 49, 350, 1.70, 0.0, 0.0, 295, 2, 'INT', 19, 11, 20, 1.8, 1.2, 2.7, 6, 2, 9, 4, 1, '由于参与到近卫军团和天灾军团的世俗争斗中，宙斯（Zeus）被众神流放到人间。虽然他的力量因此被大幅削弱，但你仍然不能小看他对于闪电的运用。同时擅长杀伤单一和多个目标，非常强大的伤害性法师英雄。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (77, '先知', '', 'Furion', 'Furion', 'furion.gif', 'display_furion.jpg', '', '近卫', '推进', '中等', '注意取消施法后摇 树人推塔断兵 大招不要直接点英雄放 疯狂利用传送带线牵制', 511, 273, 45, 59, 600, 1.70, 0.0, 0.0, 295, 4, 'INT', 19, 18, 21, 1.8, 1.9, 2.9, 6, 8, 7, 5, 1, '这个大陆充满了邪恶和杀机 所以furion没有过一刻休息的时间。作为暗夜精灵的领袖，furion不知疲倦地保卫着他神圣的森林。曾经帮助近卫军团击败了阿克蒙德的他，总会在近卫军团有危难的时刻，如人们所愿的出现在大家的眼前。他掌控着自然界狂暴的力量，并且随风而动，森林会随着他的意志来攻击敌人。与其称他为自然界的守护者或者先知，不如称他为救世主');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (78, '沉默术士', '', 'Nortrom', 'Sil', 'sil.gif', 'display_sil.jpg', '', '近卫', '控制', '中等', '技能COMBO克星 诅咒与法球压制皆可 大招无视魔免', 473, 273, 43, 57, 600, 1.70, 0.0, 0.0, 300, 1, 'INT', 17, 16, 21, 2.2, 2.1, 2.5, 6, 2, 4, 2, 2, '作为魔法王国Quel`Thalas的一员，Nortrom是保卫着大陆完整性的最强精灵战士之一。他精通剑刃投掷这一远古技艺，并将其与血精灵神秘魔法中的爆炸特性相结合，从而起到破坏敌人的魔法能量的效果。他能将他的法力附加到剑刃上，令其造成更多的伤害，并在每次击杀后获得更多的智慧。他还可以浓缩自己的能量，造成敌人魔法储藏的混乱，令他们无法施法。誓死阻挡天灾军团和他们的毁灭魔法，是他毕生奋斗的目标。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (79, '秀逗魔导师', NULL, 'Lina Inverse', 'Lina', 'lina.gif', 'display_lina.jpg', '', '近卫', 'GANK', '难', '延迟晕计算提前量 前期T住后先A用D收尾 单体高爆发', 492, 312, 37, 55, 625, 1.70, 0.0, 0.0, 295, 1, 'INT', 18, 16, 24, 1.5, 1.5, 3.2, 4, 5, 8, 6, 3, 'Lina Inverse是一位非常强大的法师，她以打击盗贼（其实是为了他们的财宝）和屠龙（一不小心毁灭了边上的城市）而著称。她能够非常熟练地投掷火球打击对手，当然她还掌握了诸如龙破斩、神灭斩等更具破坏力的法术。Lina承诺帮助近卫军团摧毁冰封王座，当然最主要是为了之后巨额的报酬——强力的武器和非常多的钱。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (80, '风暴之灵', '', 'Raijin Thunderkeg', 'Storm', 'storm.gif', 'display_storm.jpg', '', '近卫', 'GANK', '难', '大招飞行期间无敌 先手GAEARA六连 残影具备空中视野 BKB先手突击后排英雄', 511, 299, 45, 55, 480, 1.70, 0.0, 0.0, 295, 5, 'INT', 19, 22, 23, 1.5, 1.8, 2.6, 6, 7, 7, 2, 3, '作为近卫军团在最黑暗的时代所召唤的圣灵，风暴之灵决定在RaijinThunderkeg这个卑微的元素使身上证明自己的价值。由于熊猫人自身的灵魂已经在沛不可当的纯粹的电子精华的冲击下被毁灭，加上圣灵又无法使用自己的身体，就干脆降临至这个元素使的身上。尽管被凡人的躯体所束缚，风暴之灵仍然表现出极为强大的力量。通过以一种极为神秘的方式操纵广阔而无穷无尽的力之本源，在近卫军团的敌人面前召唤出毁灭性的电子火花，碾碎所有敢于在它面前出现的敌人。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (81, '风行者', '', 'alleria', 'WR', 'wr.gif', 'display_wr.jpg', '', '近卫', '推进', '难', '学会用小兵束缚英雄 单劣势路好手 万金油英雄', 435, 286, 44, 56, 600, 1.50, 0.0, 0.0, 295, 1, 'INT', 15, 17, 22, 2.5, 1.4, 2.6, 6, 6, 7, 5, 3, '在冬泉谷深处翠绿的森林里，一位拥有风之力量的精灵回到了她的故乡。在MAGI战争后，她作为一个游侠加入了近位军团。作为风神的宠儿，艾瑞拉得到了许多祝福。她精准的箭可以射穿一条直线上的所有敌人。她可以将风的力量赋予到箭上，从而将利用空气的枷锁将敌人控制在一起。利用风的涌动，她可以加快自己的速度。据说她可以把她的箭失如同狂风般释放出去,从而将敌人在一瞬间消灭干净。对于天灾来说，她确实是一个棘手的敌人。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (82, '预言者', '', 'Thrall', 'Thrall', 'thrall.gif', 'display_thrall.jpg', '', '近卫', '控制,GANK', '难', 'E有很大施法延迟 用D把对面拉回E中 大招附带沉默克制高机动 D可以秒幻象', 511, 286, 49, 53, 600, 1.70, 0.0, 0.0, 300, 1, 'INT', 19, 15, 22, 1.9, 1.4, 2.5, 5, 3, 8, 7, 2, 'Thrall作为被人类抚养长大的角斗士，萨尔拥有过人的胆识与战术，甚至强于那些年长的兽人统帅们。萨尔从人类的魔爪中逃走后遇到了格罗姆·地狱咆哮，从他的口中得知自己是霜狼氏族酋长的遗孤。萨尔重新回到他祖先生活的村庄，在那里修炼并成为了一名伟大的半兽人萨满。萨尔不仅能自由掌控闪电的力量，还可以运用特殊一能力，将其转化为魔法之墙或者是静态能量，他甚至可以将敌人的时间玩弄于股掌之间。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (83, '食人魔魔法师', '', 'Aggron Stonebreaker', 'Org', 'org.gif', 'display_org.jpg', '', '近卫', 'GANK', '易', '完全看人品', 587, 221, 58, 64, 128, 1.70, 0.0, 0.0, 295, 6, 'INT', 23, 14, 17, 3.2, 1.6, 2.4, 3, 3, 7, 7, 2, '作为石锋半兽人的首领，Aggron是近卫军团中最强壮也最聪明的英雄之一。他最特别的能力就是能将无论是伤害性的还是辅助性的法术在一瞬间多次施法，这样使得这些法术有着无比惊人的威力。对于近卫军团来说，他是这次战争中绝对不可或缺的支援型英雄。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (84, '地精工程师', '', 'Squee and Spleen', 'Goblin', 'goblin.gif', 'display_goblin.jpg', '', '近卫', '推进', '中等', '自爆流与埋雷流双路线 用E炸塔 必经之路埋雷 自爆自动释放受伤害即触发 做好拆塔与防守', 473, 286, 29, 31, 650, 1.70, 0.0, 0.0, 270, 7, 'INT', 17, 14, 22, 2.0, 1.3, 2.9, 2, 4, 6, 2, 2, '极端智慧的大脑，弥补了他们在体形上的弱势，哥布林工程师可不是好对付的。他们善于埋布肉眼不能识别的地雷；在接受强大的半兽人巫师的训练以后，又学会了放置麻痹陷阱；更不要说他们那威力强大的遥控炸弹。在和这三个小家伙作对的时候，你最好别大意。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (85, '双头龙', '', 'Jakiro', 'Thd', 'thd.gif', 'display_thd.jpg', '', '近卫', '辅助', '易', '冰封路径算提前量 线上液态火压制 TRD三连', 606, 364, 46, 54, 400, 1.70, 0.0, 0.0, 290, 2, 'INT', 24, 10, 28, 2.3, 1.2, 2.8, 2, 6, 6, 4, 3, '当人们在一个大洞里找到它时，这头巨兽还在咬着世界之树的树根。而这头华丽的奇美拉的年龄，已经无从知晓。由于它的2个大脑袋和它那充满着攻击性和不确定性的行为，Jakiro是只难以驯化的危险生物。不过现在它出于自身的原因，愿意效忠于近卫军团。它对冰元素和火元素的奇异的亲和力，来自于它奇异的神秘天性。它毁灭眼前的一切，并且能够将敌人困在一条线上。天灾军团最好对这条龙保持警惕，以免他们全被冻住并破碎，就像在他们眼前慢慢融化的寒冰王座一样。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (86, '地精修补匠', '', 'Boush', 'Tk', 'tk.gif', 'display_tk.jpg', '', '近卫', '推进,GANK', '中等', '刷新飞鞋GANK打钱 刷羊刀补控制 激光附带2秒绝对MISS', 473, 351, 49, 55, 500, 1.70, 0.0, 0.0, 305, 4, 'INT', 17, 13, 27, 2.0, 1.2, 2.2, 7, 8, 7, 2, 3, 'Boush受过最为高阶的哥布林技术的教育。那些机械系统可以帮助他在相当远的距离外杀伤对手，而相对保证自己的安全。他还可以升级他的系统使它们能更快的发射。他向你展示了，知识就是力量。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (87, '暗影萨满', '', 'Rhasta', 'Ss', 'ss.gif', 'display_ss.jpg', '', '近卫', '推进', '中等', '大招尽量用来拆塔 练习空插 不要用枷锁起手', 511, 273, 47, 54, 500, 1.70, 0.0, 0.0, 285, 1, 'INT', 19, 16, 21, 1.6, 1.6, 3.0, 5, 8, 7, 2, 2, '作为巨魔中天赋异秉的巫师，年轻的Rhasta精通各种巫术的奥义。战争爆发以后，他化身为影子猎手向近卫军团效忠并投入战斗，并决心让天灾军团万劫不复。身为一个巫师，他精通召唤群蛇守卫，束缚敌人，又或者通过使用闪电打击多个目标。他的实力已经全面超越了他的师傅，无愧于“天才般的影子猎手”这个称号。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (88, '大魔导师', '', 'Rubick', 'GM', 'gm.gif', 'display_gm.jpg', '', '近卫', 'GANK', '中等', '偷强力技能 Z之后一定要选落点 使用X压制', 511, 351, 44, 54, 600, 1.70, 0.0, 0.0, 290, 1, 'INT', 19, 14, 27, 1.5, 1.6, 2.4, 5, 6, 8, 6, 2, '拉比克是啊哈利姆的儿子，一个不可忽视的站立。人们惊叹于他的能力，可以随心所欲抓起和投掷物体而仅仅只靠念力，许多巫师曾向他提出挑战，但都败给了他强大的弱化魔法。拥有无尽才能的大魔导师，绝不会因为敌人的出现而止步，无比智慧的他会运用一切方式，哪怕是施展敌人的魔法来击倒敌人，在只靠一项就偷取技能方面享有盛誉的大魔导师，是近卫军团对抗他i安在军团最无可争议的瑰宝。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (89, '天怒法师', '', 'Skywrath Mage', 'SM', 'sm.gif', 'display_sm.jpg', '', '近卫', 'GANK', '难', '利用距离控制震荡射击 大招接队友先手晕', 511, 312, 36, 46, 600, 1.70, 0.0, 0.0, 315, 1, 'INT', 19, 18, 27, 1.5, 0.8, 3.2, 5, 3, 8, 6, 4, '扎贡纳斯是一名足智多谋的天怒法师，十分擅长魔法战斗。出于其神秘的天性和无法猜测的动机，他加入了近卫军团对抗天灾军团的战斗中。据说他来自血精灵族中一支非常古老的血脉，古老到已经完全达到了神秘魔法的最高造诣。有人怀疑扎贡纳斯和祈求者同样是上古时期的两位魔法天才，为了法师的最高荣誉而争斗。他对魔法超乎寻常的理解能够让他直接封印魔法，他强大的神秘之耀能够在短短几秒内将敌人化作灰烬。他的盟友都因为能和他站在同一战线而自豪无比，而他的敌人，则根本不能在他那强大的魔法下存活。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (90, '痛苦之源', '', 'Atropos', 'Bane', 'bane.gif', 'display_bane.jpg', '', '天灾', '控制,GANK', '难', '虚弱技能减攻击 噩梦配合后手技能 大招无视魔免', 492, 234, 51, 57, 400, 1.70, 0.0, 0.0, 305, 4, 'INT', 18, 18, 18, 2.1, 2.1, 2.1, 2, 2, 8, 3, 3, 'Atropos是将噩梦和恐惧散播到这个世界的原始力量。伴随他出现的必定是无止境的恐怖和慌乱。他可以通过侵蚀对手的精神医疗自己；他也能通过挖掘对手内心的恐惧大幅削弱对手的作战能力；将对手送入无边的噩梦使其完全瘫痪是他最为得意的事情。在必要的时候，他甚至肯撕裂自己的灵魂，将它注入对手体内，使对手完全不能动弹并承受巨大的绝望和痛苦。一言以蔽之，Atropos就是恐怖的同义词。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (91, '黑暗贤者', '', 'Ish\'kafeI', 'DS', 'ds.gif', 'display_ds.jpg', '', '天灾', '推进', '难', '真空接COMBO 3技能奔跑极速 离子烫打野GANK', 568, 377, 60, 66, 128, 1.70, 0.0, 0.0, 300, 6, 'INT', 22, 12, 29, 2.3, 1.2, 2.7, 2, 4, 4, 6, 2, '被族人所流放的黑暗贤者是天灾军团最会算计和耍诈的英雄，也是最最神秘的，除了巫妖王不和任何人说话。他另类的的才能极为特殊和灵魂，在战斗中能提供攻击型和防御型两种魔法，甚至在一场战斗刚刚开始之前，已经将局势逆转。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (92, '死亡先知', '', 'Krobelus', 'DP', 'dp.gif', 'display_dp.jpg', '', '天灾', '推进', '易', '大招尽量用来拆塔 大招结束回血 目标越近，大招伤害越高', 511, 260, 44, 56, 600, 1.70, 0.0, 0.0, 285, 3, 'INT', 19, 14, 20, 2.2, 1.4, 3.0, 7, 9, 6, 2, 5, 'Krobelus生前是一个狂热的食死徒。在她死后，她的信仰与她的死亡完美地结合起来，使她已死的幽魂能够以一种混乱的生命形式来发泄怒火，肆意破坏。她能够召唤腐臭蜂群和孤魂野鬼，并用它们来埋葬那些血管中仍然有血液搏动的人们。她能用巫术增强自己的力量，并且能让敌人因恐惧而瞠目结舌，使他们失去施放法术最需要的东西---声音。所有迹象表明，她已投身于死亡，并乐意看到一切东西都化为灰烬。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (93, '恶魔巫师', '', 'Lion', 'Lion', 'lion.gif', 'display_lion.jpg', '', '天灾', 'gank', '难', '穿刺接大 跳刀羊', 454, 286, 42, 48, 600, 1.70, 0.0, 0.0, 290, 1, 'INT', 16, 15, 22, 1.7, 1.5, 3.0, 3, 3, 9, 5, 2, '通过掌控来自阿克蒙德的可怕的力量，Lion无疑是一个值得敬畏的对手。他曾经是Dalaran的一个魔法师，在向未知的魔法领域探索的过程中受到冰封王座强大意识的腐化而投向邪恶。他精通各类阻碍性魔法，善于使用各种诅咒，他对黑暗魔法的掌握已经到达极致。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (94, '谜团', '', 'Darchrow', 'Enigma', 'enigma.gif', 'display_enigma.jpg', '', '天灾', '推进', '难', '跳刀开大 1级打野 大招无视魔免', 473, 260, 42, 48, 500, 1.70, 0.0, 0.0, 300, 4, 'INT', 17, 14, 20, 2.1, 1.0, 3.4, 3, 7, 7, 5, 2, '当一颗即将陨落的恒星进入一片充满着元素魔法的区域之后，一种最危险的生命体便产生了。这种生命体的名字叫做谜团。出于怨恨与饥饿，他能够在自己盟军的身上开启空间传送门，让他的仆从进入这个世界。在战斗中，他操纵着虚空与暗影的力量，甚至释放出黑洞，将所有靠近黑洞的敌人吸入无底的深渊。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (95, '巫妖', '', 'Kel\'Thuzad', 'Lich', 'lich.gif', 'display_lich.jpg', '', '天灾', 'gank,辅助', '易', '放大的时机 吃兵控线 小兵冰盾推进流', 492, 234, 42, 51, 550, 1.70, 0.0, 0.0, 315, 1, 'INT', 18, 15, 18, 1.6, 2.0, 3.2, 2, 3, 7, 7, 2, '身为被巫妖王的强力魔法复活过来的精英级守卫，Kel`Thuzad能够如臂使指地操纵诺森兰的严寒。他生前操控寒冰气流的天赋并没有因为他的死亡而消失。由于对寒冰魔法的极度精通，使得他能够将寒气聚集在一个球体上，并让它在敌军之中四处跳跃造成极大的杀伤力，令那些敌人体验到他复活时所遭受的巨大痛苦。他可以为了恢复魔力而不惜牺牲自己的盟友，是个不折不扣的冷血杀手。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (96, '瘟疫法师', '', 'Rotund`jere', 'Nec', 'nec.gif', 'display_nec.jpg', '', '天灾', '推进', '中等', '推进阵容核心 大招克肉盾 站住就是输出', 454, 286, 44, 48, 550, 1.70, 0.0, 0.0, 290, 1, 'INT', 16, 15, 22, 2.0, 1.7, 2.5, 4, 9, 6, 5, 5, '在被一些自命神圣的人作为异端处决以后，死亡带给Rotund`jere的只有对生命的深沉恨意，他通过将恶灵送入敌人体内来表达这股恨意。每当一个生命在他手中终结的时候，他都感到无比的满足并因此变得更为强大。他走的每一步所散发出来的死亡的气息，使周围的生物随着他的出现而腐败枯萎。这位折磨和痛苦的大师僵硬的脸上始终挂着一副令人作呕的笑容，他毫不留情地杀死他所看到的任何弱小生命。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (97, '湮灭法师', '', 'Pugna', 'Pugna', 'pugna.gif', 'display_pugna.jpg', '', '天灾', '推进', '难', '选好棒子位置抽蓝反弹 衰老增加法伤 反法系团推首选', 473, 338, 45, 53, 600, 1.70, 0.0, 0.0, 320, 1, 'INT', 17, 16, 26, 1.2, 1.0, 4.0, 3, 9, 5, 5, 3, '在被强力的魔法复活以后，Pugna还保留着些许过去的记忆，不过他的样子实在已经令人不忍直视。通过使用还残留着印象的死灵魔法来折磨对手，这具踉踉跄跄的骷髅从虐待他人中感到了无比的满足。他先将对手送入痛苦的异次元，再以邪恶能量的爆轰迎接他的回归。那些劫后余生的人们常常带着恐惧的眼神向别人诉说，Pugna是如何----说好听些吧----汲取活人的生命的。这带给Pugna短暂的但是实实在在活着的感觉。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (98, '黑曜毁灭者', '', 'Harbinger', 'DES', 'des.gif', 'display_des.jpg', '', '天灾', '后期', '中等', '团队加魔机 神圣伤害暴力输出 用禁锢躲技能', 511, 338, 49, 64, 450, 1.70, 0.0, 0.0, 300, 5, 'INT', 19, 24, 26, 1.8, 2.0, 3.3, 9, 3, 5, 4, 2, '在偶然地被巫妖王的亡灵法师们赋予了生命后，这具黑曜石雕像便成了一头依靠吸食周围魔法为生的无情的怪物。因此，它天生就有强大的魔法控制力，可以在施法的时候被动恢复它和它盟友的法力。它骄傲并狡猾，非常喜欢囚禁敌人并剥夺他们的智力。这个黑暗并嗜血的生物，从来就不知道什么叫做手下留情，任何试图与它战斗的人都再看不到明天的太阳。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (99, '痛苦女王', '', 'Akasha', 'Qop', 'qop.gif', 'display_qop.jpg', '', '天灾', 'gank', '难', '中单毒标压制 团战等一轮技能再切入 吼和大招尽量打更多人', 454, 312, 45, 53, 550, 1.70, 0.0, 0.0, 300, 2, 'INT', 16, 18, 24, 1.7, 2.0, 2.5, 4, 5, 8, 2, 2, 'Akasha是死于霜之哀伤剑下的无数亡魂之一。在被巫妖王复活成女妖之后，她决定为自己寻找新的躯体。在森林深处，她成功地征服了一个魔女，并占据了她的肉身。她最喜欢用魔女的毒击和女妖的嚎叫折磨敌人，是名副其实的“痛苦女王”。Akash刺耳的尖叫能震破敌人的耳膜，有些大声的尖叫甚至能产成强力的声波，使地表泛起波纹，将它们所经过的一切东西都震得粉碎。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (100, '术士', '', 'Demnok Lannik', 'WL', 'wl.gif', 'display_wl.jpg', '', '天灾', '辅助', '易', '团战上致命链接 团战吟唱剧变 大部分情况团战先手大 大招无视魔免', 492, 312, 46, 56, 600, 1.70, 0.0, 0.0, 295, 2, 'INT', 18, 10, 24, 2.5, 1.0, 2.7, 2, 2, 6, 9, 3, '作为被Mannoroth长期控制业已衰落的黑日部落名义上的首领，Lannik加入天灾军团以回报巫妖王，旧日盟友暗月部落的首领的恩情。凭借着对召唤恶魔与毁灭术精通，他尽情享受着折磨那些光之子民的快乐，将他们的生命之火如同烛火般捻灭。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (101, '暗影恶魔', '', 'Eredar', 'Eredar', 'eredar.gif', 'display_eredar.jpg', '', '天灾', 'gank', '难', 'D躲技能 W开视野叠加伤害 大招留人并消除BUFF', 473, 338, 53, 57, 500, 1.70, 0.0, 0.0, 295, 3, 'INT', 17, 18, 26, 1.9, 2.2, 2.7, 6, 5, 8, 4, 2, '远在近卫天灾开战之前，这个名叫艾瑞达的远古恶魔，就已经开始散播仇恨挑起战争。没有任何正义之士可以敌过他，但他却被他的部下背叛而遭到杀害。然而，他依靠残存的一片影子复生了；经历数千年，艾瑞达恢复了他大部分的形态，力量也每天在增强。但是在命运的安排下，他化身为豺狼。艾瑞达现在的形态不适合与敌人正面冲突，但他知道如何运用各种诡计来招待他们——放逐、毒药、诅咒与邪恶魔法。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (102, '蝙蝠骑士', '', 'Batrider', 'Bat', 'bat.gif', 'display_bat.jpg', '', '天灾', 'GANK', '中等', '大招无视魔免 粘油叠加伤害高 火焰路径配合粘油追杀 粘油可显示隐形单位位置', 587, 312, 48, 52, 375, 1.70, 0.0, 0.0, 290, 2, 'INT', 23, 15, 24, 2.7, 1.5, 2.5, 6, 6, 8, 3, 3, '曾经的肉体炸弹，Jin\'zakk现在以经不再像其他蝙蝠骑士一样，仅仅是为了牺牲而存在，它创造了自己新的战斗方式：控制自己的能力，并精确打击自己的敌人，而不再是肆意毁灭周围的事物。多年的战斗经验可以使得它随心所欲运用火，并根据自己的需要创造出各种新的能力：它可以在敌人的头顶浇注粘稠的燃油，在它们逃离之前点燃它们，或者用炽焰将敌人炸飞，或者在它自己飞行的轨迹后面留下一道火焰来灼烧那些敢于跟随它的敌人。然而，它最至命能力是将某个不幸敌人关入烈焰制造的牢笼之中，并将它拖入己方阵营的深渊之中。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (103, '暗影牧师', '', 'Dazzle', 'SP', 'sp.gif', 'display_sp.jpg', '', '天灾', '推进,治疗', '难', '治疗波攻防一体 剧毒之触两次晕 全技能物理伤害 大招减甲开视野 薄葬保命', 454, 351, 41, 59, 500, 1.70, 0.0, 0.0, 305, 2, 'INT', 16, 21, 27, 1.8, 1.7, 3.4, 2, 2, 4, 7, 3, '在天灾军团入侵以前，Dazzle作为一个隐蔽的巨魔部落的成员以精通辅助魔法而著称。但是，在他为了活命同巫妖王签订了邪恶的契约之后，他渐渐堕落。凭借着粉碎敌人的防御和增强盟友使他们更加持久的战斗的能力，他在战场上的作用绝不可轻视。有谣言称他能复活已经死去的盟友，让他们为捍卫冰封王座的荣耀再次冲锋。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (104, '祈求者', '', 'Kael', 'Kael', 'kael.gif', 'display_kael.jpg', '', '天灾', '控制', '难', '10个技能切换要练熟 技能多连用 吹风磁暴消耗 天火陨石输出 急冷冰墙补控 火人迅雷推进 幽灵漫步逃命', 511, 286, 35, 41, 600, 1.70, 0.0, 0.0, 280, 2, 'INT', 19, 20, 22, 1.7, 1.9, 2.5, 7, 7, 5, 3, 4, 'Quas,Wex,Exort. 风暴的精华。 暴风雪之心。 凤凰的灵魂。在他的子民们分崩离析到世界的各个角落去之后，Kael被遗弃了，陪伴他的是他曾经创造的的大世界仅存的遗物。对复仇的渴望，让他将自己的灵魂出卖给了耐奥祖，交换来的是近乎无限的力量。披上古代血法师的斗篷，创世者只带着他支配的元素跨入了这场战争。他能够将元素熔合成致命的咒语，在狂怒中撕裂天空。他的天才无穷无尽，近卫军团将会体会到创世者的愤怒。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (105, '死灵飞龙', '', 'Visage', 'Visage', 'visage.gif', 'display_visage.jpg', '', '天灾', 'GANK', '难', '小龙多线操作 团战满灵魂无限C 小龙全球支援', 568, 273, 45, 55, 600, 1.70, 0.0, 0.0, 295, 1, 'INT', 22, 11, 24, 2.4, 1.3, 2.5, 5, 6, 7, 5, 6, '在被一个恶毒的鹰身女妖带回到死灵世界以后，这只曾经高傲的飞龙受到了混乱力量的腐蚀，成为了操纵死亡的大师Visage，只有他的形体还保留了下来。墓穴中邪恶的战栗使他陷入杀戮的狂暴中。通过共鸣，他可以吞食鲜活的灵魂治愈自身，或是将他们连接起来以分担伤害。他还能够将死去同伴的灵魂注入尸体中，使他们为他而战。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (106, '受折磨的灵魂', '', 'Leshrac the Malicious', 'Ts', 'ts.gif', 'display_ts.jpg', '', '天灾', '推进', '难', 'C无视魔免，单挑神技 用C拆塔速度极快 四个AOE技能', 454, 338, 45, 49, 600, 1.70, 0.0, 0.0, 310, 3, 'INT', 16, 23, 26, 1.5, 1.7, 3.0, 2, 9, 5, 6, 3, '痛苦围绕着我 痛苦寄身于我 痛苦常伴我左右 让我们征服我们的敌人吧！ 他们不理解我们所受的折磨 你能理解？ 不，你不能 任何灵魂都不能 正因为这样 我们要让他们也感受一下 这样的痛苦…… 这盘踞我们灵魂深处的 痛苦');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (107, '巫医', '', 'Vol\'jin', 'WD', 'wd.gif', 'display_wd.jpg', '', '天灾', 'GANK,辅助', '中等', '对面人数>1时扔C最好 开巫毒回复术对点 选好放大时机避免被打断', 454, 312, 51, 61, 600, 1.70, 0.0, 0.0, 305, 1, 'INT', 16, 13, 24, 1.8, 1.4, 2.9, 4, 3, 6, 5, 3, '所有和Vol\'jin打交道的人都知道，他不在乎冒风险，做为一个巫毒术的巫师，有一定的觉悟是必要的，但完全不懂得理智的重要就太糟糕了。Vol\'jin在巫毒术的研究上取得了辉煌的成果，包括治愈伤口的能力，对能弹射数米远的麻醉性药剂的运用以及最惊人的，能剥夺生命力的诅咒，得到如此强大的力量是不可能不引起注意的，在Vol\'jin的实验变的越来越不稳定，越来越具毁灭性时，巫妖王开始动手了。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (108, '极寒幽魂', '', 'Ancient Apparition', 'AA', 'aa.gif', 'display_aa.jpg', '', '天灾', 'GANK', '难', '大招全球支援 大招尤其克制治疗类英雄 与队友配合使C结冰', 492, 325, 43, 54, 600, 1.70, 0.0, 0.0, 295, 2, 'INT', 18, 20, 25, 1.4, 2.2, 2.6, 3, 4, 8, 3, 2, '在一次远古的战役中战败，这个曾经无比强大的法师被永恒的封印在了一块冰的墓碑下。在无法计数的岁月里，他躺在深入骨髓的痛苦里，直到他被巫妖王的军队发现。巫妖王为了讨好这远古的灵魂，将他释放了出来，虽然只是徒劳。因为这个灵魂不再包容着感激，他无法区分敌人和朋友，他只知道什么是痛苦。由于他的身躯早已腐烂，他使用了他知道的唯一形态。这个被憎恨侵蚀的生物即使碰触下敌人都会引起发自灵魂的剧痛。他仅通过思想就可以召唤威力无比的暴风雪和从遥远的距离发出的摧毁灵魂的寒冰箭。这个生物惩罚所有敢于站在他面前的生命。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (109, '弧光守望者', '', 'Zet, the Arc Warden', 'AW', 'aw.gif', 'display_aw.jpg', '', '天灾', '推进,Gank', '难', '放大点金.. 道具技能流 红杖死灵羊刀.. 挑战APM', 606, 312, 40, 50, 625, 1.70, 0.0, 0.0, 295, 0, 'INT', 24, 15, 24, 1.9, 1.8, 2.1, 5, 8, 7, 5, 4, '在遥远的南方，有一块充满着以太和磁场之力的徒弟，风暴无尽的在这片土地上肆虐。在这里，只有一个游牧民族能够或下来并定居。这些游牧民族的人崇拜撕裂大地的风暴，在这种诡异的力量和虔诚的信仰中，一个混乱的神祗诞生了。神祗感受到了即将到来的战争，于是他派出他最信任的先知，赋予他自己的力量，让他去镇压那股威胁到这混乱神祗的秩序势力。拥有闪电、磁场的力量，同时还能完美的复制自我，弧光守望者是一个无法被忽视的存在。');
INSERT INTO `hero` (`id`, `zh_name`, `zh_short`, `en_name`, `en_short`, `avatar`, `display`, `tavern`, `camp`, `skilled_at`, `complex_rate`, `tags`, `init_hp`, `init_mp`, `init_min_damage`, `init_max_damage`, `attack_range`, `attack_speed`, `attack_pre_anim`, `attack_post_anim`, `move_speed`, `init_armor`, `major_attr`, `init_strenth`, `init_agile`, `init_intelligence`, `inc_strenth`, `inc_agile`, `inc_intelligence`, `dps_rank`, `push_rank`, `gank_rank`, `support_rank`, `tank_rank`, `desc`) VALUES (110, '寒冬飞龙', '', 'Auroth, the Winter Wyvern', 'WW', 'ww.gif', 'display_ww.jpg', '', '天灾', 'Gank', '中等', '笑看16级冰龙 大招强点控', 606, 325, 38, 45, 425, 1.70, 0.0, 0.0, 285, 1, 'STR', 24, 16, 25, 2.1, 1.9, 2.4, 6, 6, 7, 5, 3, '被诺森德的死灵法师复活并重塑成尸骨之龙之后，寒冬之龙仍旧拥有霜冻之力。为了歼灭世间一切闪耀着温暖生命光芒的事物，寒冬飞龙开始攻击近卫军团。他有着最纯粹的冰霜元素之力，其寒冷的呼吸能给盟友带去生命的活力，也能给敌人带去令人发狂的严寒，让他们自相残杀。他还能发射寒冰碎片，让敌人的军队寸步难行。这样一种邪恶的、强大的、拥有致命冰霜之力的生物，就连天灾军团都不知道这样的怪兽当时是怎么死去的。');
