DROP TABLE IF EXISTS `item_skills`;
CREATE TABLE `item_skills` ( `id` INT(11) NOT NULL PRIMARY KEY , `item_id` INT(11) NOT NULL, `ord` INT(11) NULL DEFAULT '1', `name` VARCHAR(20) NOT NULL, `positive` TINYINT(1) NULL DEFAULT '0', `distance` VARCHAR(11) NULL DEFAULT '0', `scope` VARCHAR(11) NULL DEFAULT '0', `remain` VARCHAR(11) NULL DEFAULT '0', `cast` VARCHAR(11) NULL DEFAULT '0', `cooldown` VARCHAR(11) NULL DEFAULT '0', `desc` TEXT NULL );

