DROP TABLE IF EXISTS `items`;
CREATE TABLE `items` ( `id` INT(11) NOT NULL, `name` VARCHAR(40) NOT NULL, `hotkey` VARCHAR(1) NOT NULL, `clickable` TINYINT(1) NOT NULL DEFAULT '0', `mana` INT(11) NULL DEFAULT '0', `cost` INT(11) NULL DEFAULT '0', `property` TEXT NULL, `notice` TEXT NULL, `desc` TEXT NULL, `recipe_id` INT(11) NULL DEFAULT NULL, `recipe_cost` INT(11) NULL DEFAULT NULL, `desc_add` TEXT NULL );


