INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (36, 9, '裂地者', 4, 1, 'E', '牛头人酋长挥动斧子, 以不可思议的力量猛击地面, 产生裂纹, 裂纹会在地表向前扩张, 3秒后, 裂纹会向内部塌陷, 附近的敌方单位均会陷入其中, 受到自身最大生命值35%的伤害, 并被减速. ', '伤害及减速效果无视魔法免疫', '', '', '1600', '2400', '', '', '325', '', '100', '175', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (37, 10, '大自然的掩护', 1, 0, 'T', '改变一个友方单位的外形, 将其掩于树林之中, 该单位会保持隐身状态, 移动速度提升10%, 直到其攻击或者离开树林周围为止. ', '', '', '', '300', '', '', '', '', '', '10/8/6/4', '90/80/70/60', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (38, 10, '寄生种子', 2, 0, 'E', '树精卫士在敌人身上种下寄生种子, 寄生种子减缓被寄生单位24%的移动速度, 能在接下来3秒内汲取该敌人的生命能量, 并转移给其附近500范围内的友方单位. <br/>一共汲取4次. ', '', '', '', '350', '', '', '', '500', '', '12', '140', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (39, 10, '活体护甲', 3, 0, 'V', '为友方单位创造一个魔法藤蔓护甲，保护他不受伤害并医治伤口。承受6次攻击后失效。', '可以对建筑使用', '', '', '全地图', '', '', '', '', '20', '20', '30/35/40/45', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (40, 10, '疯狂生长', 4, 1, 'R', '让树精卫士四周的藤蔓和树枝疯狂地生长, 缠绕周围的敌方地面单位. 被缠绕的单位不能移动和攻击(可以使用技能). ', '牵制效果无视魔法免疫', '', '', '', '', '', '', '625', '', '80', '150/175/200', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (41, 11, '闪电之链', 1, 0, 'T', '精灵守卫将自己与一个友军单位用闪电之链连接起来, 使两者共同提升20%的移动速度; 精灵守卫获得的任何恢复状态, 也同时会影响到被连接的单位(1.5倍效果), 任何接触到闪电之链的敌方单位会被眩晕. <br/>如果双方的距离超过900, 则链接会破裂. ', '', '', '', '', '', '', '', '', '12', '12', '40', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (42, 11, '幽光之魂', 2, 0, 'W', '精灵守卫耗时4秒召唤出共计5个幽光之魂, 每个幽光之魂都会对逼得过近的敌人展开反击如果是英雄单位, 幽光之魂会释放自己的生命能量引发爆炸, 对附近300范围内所有敌人造成伤害; 如果是非英雄单位, 幽光之魂只会对其造成轻微伤害, 并不爆炸. ', '', '', '', '', '', '', '', '', '10', '20/18/16/14', '120/130/140/150', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (43, 11, '过载', 3, 0, 'V', '精灵守卫汲取超过其可以安全掌控的能量, 以自身流失当前生命与魔法3.5%/秒的代价, 提升攻击速度和伤害减免, 如果存在被闪电之链连接的单位, 则该单位也会获得相同的提升效果. ', '', '', '', '', '', '', '', '', '', '', '', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (44, 11, '守卫降临', 4, 1, 'R', '精灵守卫将自身和被闪电之链连接的英雄单位(如果有的话)传送至目的地, 经过一段时间后会自动回到原地. <br/>施法时目的地会出现印记, 对敌人可见. ', '', '', '', '全地图', '', '', '', '', '12', '90/75/60', '100', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (45, 12, '酸性喷雾', 1, 0, 'D', '在目标区域形成高腐蚀性的酸性雾气. 逗留被腐蚀区域的敌方单位将持续受到伤害, 护甲同时被降低. ', '无视魔法免疫', '', '法术攻击普通伤害', '900', '', '', '', '625', '16', '22', '130/140/150/160', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (46, 12, '不稳定化合物', 2, 0, 'E', '炼金术士花一些时间把化学药剂摇匀, 然后抛向敌人, 对其造成伤害和眩晕. 开始摇晃药剂后, 每多摇一秒, 药剂就能多造成一些伤害和眩晕, 同时炼金术士身体也会变得越红; 最多摇5秒. 5秒后药剂调配完成, 有1.5秒的缓冲期. 一旦缓冲期结束, 药剂将自行爆炸. 不稳定化合物爆炸时将影响175范围内的单位（包括炼金术师自爆时）', '伤害无视魔法免疫', '敌军将无法看到不稳定化合物倒计时', '英雄攻击普通伤害', '', '', '', '', '', '', '16', '120', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (47, 12, '地精的贪婪', 3, 0, 'G', '贪婪成性的炼金术士在杀死敌方单位时, 能额外到得金钱奖励; 在25秒内连续杀死单位更可以提高2金钱奖励额度, 上限26. 地精的贪婪金钱加成为4/6/8/10 + 1/2/3/4', '', '', '', '', '', '', '', '', '', '', '', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (48, 12, '化学狂暴', 4, 1, 'R', '炼金术士用化学药剂让他的食人魔坐骑陷入狂暴, 得到额外的生命值奖励, 降低基础攻击间隔, 提高基础生命和魔法回复速度. 大幅提升速度. ', '', '', '', '', '', '', '', '', '25', '45', '50/100/150', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (49, 13, '弹幕冲击', 1, 0, 'E', '发条地精从机械结构的开口处喷出高能弹片, 每0.7秒对附近的随机的一个敌方单位造成一定的伤害和0.01秒的晕眩. ', '', '', '', '', '', '', '', '275', '10.5', '32/28/24/20', '75', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (50, 13, '能量齿轮', 2, 0, 'C', '发条地精以弹出的内部核心零件开成屏障环绕在他的四周, 将附近的敌方单位困在其中. 这些齿轮在消失前需要受到3次攻击才能完全摧毁. 如果带有魔法值的敌方单位尝试从外侧靠近其中任意一个齿轮, 就会损失一定的生命和魔法, 并被击退. ', '', '', '', '', '', '', '', '', '5/6/7/8', '15', '', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (51, 13, '照明火箭', 3, 0, 'R', '向指定区域发射一枚快速的照明火箭, 对目标范围内的敌方单位造成伤害. 照明火箭提供视野, 持续10秒. ', '', '', '', '全地图', '', '', '', '575', '', '20/18/16/14', '50', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (52, 13, '发射钩爪', 4, 1, 'T', '向目标或者指定区域射出一个可以伸缩的钩爪, 接触任意单位即缩回. 如果这是一个非中立的敌方(友方或非中立的敌方)单位, 钩爪会将其牢牢抓住并将发条地精牵引至这个单位的身边, 并对敌方单位造成伤害和晕眩. (括号内为装备阿哈利姆神杖时的数值)', '', '', '', '', '', '', '', '', '', '70/55/40(15)', '150', '减少冷却时间并且能钩友方单位', NULL);
