INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (55, 14, '龙族血统', 3, 0, 'D', '龙骑士身上所流的龙族之血使他有更高的生命回复速度和护甲. ', '', '', '', '', '', '', '', '', '', '', '', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (56, 14, '真龙形态', 4, 1, 'R', '龙骑士弯身为龙, 提升25%的基础移动速度, 获得特殊能力. 龙拥有远程攻击能力, 射程500. <br/>变形后增加神龙摆尾的施法距离至400. ', '', '', '', '', '', '', '', '', '60', '115', '50', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (57, 15, '活血术', 1, 0, 'V', '以魔法激发目标单位身体内潜在的恢复力量, 根据他的主属性恢复生命, 当目标生命值低于40%的时候将得到更快的恢复速度. (括号内为血量低于40%时的数字)', '能对魔免释放', '', '', '400', '', '', '', '', '16', '25', '170', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (58, 15, '沸血之矛', 2, 0, 'R', '用自己的鲜血让矛燃烧, 如跗骨之蛆般使敌方单位受到持续性伤害, 持续7秒. <br/>消耗自身15点生命. <br/>可以无限次叠加. ', '法球效果不能叠加', '攻击特效不能叠加', '', '', '', '', '', '', '', '', '', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (59, 15, '狂战士之血', 3, 0, 'B', '神灵武士将所承受的每一分创伤和疼痛转变为自身力量的提升, 每损失7%的血量就能被动提升攻击速度, 并增加攻击力. <br/>满血时已得到一次提升. ', '', '', '', '', '', '', '', '', '', '', '', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (60, 15, '牺牲', 4, 1, 'F', '神灵武士向目标发动冲刺, 变得魔法免疫, 以伤害自身为代价, 来对目标造成当前血量50(65)%的伤害, 并降低其50%的移动速度, 持续5秒. (括号内为装备阿哈利姆神杖时的数值)', '减速效果无视魔法免疫', '', '', '550', '', '', '', '', '', '45(4)/30(4)/15(4)', '', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (61, 16, '粘稠鼻液', 1, 0, 'G', '用鼻涕包裹一个敌方单位, 降低他的移动速度, 减少其护甲. 效果可以累积4次, 持续5秒. ', '', '', '', '600', '', '', '', '', '1.5', '', '30', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (62, 16, '刺针扫射', 2, 0, 'R', '将身上的尖刺发射出去, 对625范围内的敌方单位造成伤害. 10秒内再次受到刺针扫射的打击会叠加30点额外的伤害, 最高可以叠加到400点伤害. ', '伤害无视魔法免疫', '', '英雄攻击普通伤害', '', '', '', '', '650', '', '3', '35', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (63, 16, '刚毛后背', 3, 0, 'K', '刚背兽的后背有刚毛覆盖, 减少他所受到的伤害. <br/>刚毛后背积累伤害超过250后, 后背/侧面再次承受一次攻击的时候将触发一次次刺针扫射. 不能减少来自建筑的伤害. <br/>钢毛后背现在会在承受伤害达到临界值的同时触发刺针扫射，而不是以前的超过临界值才触发', '', '', '', '', '', '', '', '', '', '', '', '', NULL);
