INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (232, 61, '魂之挽歌', 4, 1, 'R', '召唤恶灵以均匀的角度向四周发射造成伤害, 越近的单位受到越多恶灵的打击, 同时降低700范围内敌方单位的移动速度和攻击力, 持续5秒. 发射出的恶灵数目为储存的灵魂数目的一半. <br/>在影魔死去的时候他将要失去的灵魂会被释放出来，效果等同魂之挽歌。这个效果不会消耗魂之挽歌的冷却时间， 是魂之挽歌的一个被动效果。', '', '', '', '', '', '1', '', '', '', '120/110/100', '150/175/200', '', NULL);
