INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (237, 63, '幽鬼之刃', 1, 0, 'D', '幽鬼掷出致命的匕首, 匕首对划过的敌方单位造成伤害并留下一条暗影之径. 被匕首击中的敌方英雄在移动时也会留下一条暗影之径. 幽鬼在暗影之径上进入相位移动的状态, 无视地形. 暗影之径同时提升幽鬼/降低敌方单位的移动速度. ', '', '', '', '2000', '', '', '', '', '7', '16', '130/140/150/160', '', NULL);
