INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (246, 65, '幽冥剧毒', 2, 0, 'R', '在攻击中附加强烈毒素, 被攻击目标当前生命值越低, 受到的伤害就越多. <br/>伤害值为技能等级*伤害系数, 生命百分比每下降20%, 伤害系数翻倍, 初始伤害系数为2. <br/>对非英雄单位造成一半的伤害. ', '无视魔法免疫<br/>对建筑物有效', '', '', '', '', '', '', '', '', '', '', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (247, 65, '腐蚀外表', 3, 0, 'E', '冥界亚龙的身体由毒素覆盖, 任何攻击他的单位都会受到侵蚀, 攻击速度和移动速度因此被降低, 并受到毒素伤害, 持续4秒. 毒素同时能帮助吸收部分魔法伤害. ', '无视魔法免疫', '', '', '', '', '', '', '', '', '', '', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (248, 65, '蝮蛇突袭', 4, 1, 'V', '恶毒地撕咬目标, 大幅降低目标的攻击速度和移动速度, 造成毒性伤害. (括号内为装备阿哈利姆神杖时的数值)', '减速效果无视魔法免疫', '', '', '500(900)', '', '', '', '', '5', '80(12)/50(12)/30(12)', '125(125)/175(125)/250(125)', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (249, 66, '地之束缚', 1, 0, 'E', '向目标区域撒落大量的地刺, 束缚作用范围内的敌方单位的行动. 他们不能移动, 不过能攻击并使用技能, 持续2秒. ', '', '', '', '', '', '', '', '220', '', '20/16/12/8', '100', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (250, 66, '忽悠', 2, 0, 'F', '通过汲取大地的神秘能量, 地卜师的主身和分身可以互相传送, 并在消失和出现的地点附近375范围内留下毁灭性的能量, 造成伤害. ', '', '', '', '全地图', '', '', '1.5', '', '', '14/12/10/8', '80', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (251, 66, '地之突袭', 3, 0, 'G', '地卜师的武器经过大地精魄的强化, 在粉碎敌人生命的同时麻痹他们的双腿, 降低他们的移动速度并造成伤害, 持续2秒. 各个地卜师的技能效果可以互相叠加. ', '无视魔法免疫', '攻击特效不能叠加', '', '', '', '', '', '', '', '', '', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (252, 66, '分则能成', 4, 1, 'D', '地卜师按照自己的样子制造一个不完美的复制品. 他的克隆可以像正常的英雄一样得到金钱和经验并共享给地卜师, 甚至克隆还可以使用地卜师的所有技能. 遗憾的是, 克隆唯一能分享的装备, 是地卜师真身所装备的鞋类物品. 克隆所得到真身的附加属性的30%的加成. (括号内为装备阿哈利姆神杖时的数值)', '不论真身或者克隆死亡, 所有的真身和克隆会一同死亡', '', '', '', '', '', '', '', '', '', '', '额外制造一个复制品', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (253, 67, '等离子场', 1, 0, 'F', '闪电幽魂释放出威力正比于其扩张程度的高能等离子体. 在等离子体扩张和收缩过程中, 均会对处于其中的敌方单位造成伤害(一共两次). 等离子体离闪电游魂的距离越远, 伤害越大. ', '', '', '', '', '800', '', '', '', '', '14', '125', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (254, 67, '静电连接', 2, 0, 'C', '闪电幽魂将能量渗入目标英雄体内, 形成一个充能的链接. 只要该链接存在, 闪电幽魂就会持续从目标英雄体内偷取攻击力并化为己用, 链接最多存在8秒, 偷取的攻击力最多保留18秒. ', '', '', '', '600', '700', '', '', '', '', '25', '20/30/40/50', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (255, 67, '不稳定电流', 3, 0, 'N', '闪电幽魂的身体由不稳定的暗能量构成, 这反自然的状态能使他以超自然的速度移动, 而且, 如果有哪个愚蠢的敌人对他施展指向性技能, 他将会为自己的冒失付出代价被反馈回来的电流好好“奖赏”. ', '6.75这个技能有很多改动哦!', '', '', '', '', '', '', '900', '', '', '', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (256, 67, '风暴之眼', 4, 1, 'E', '闪电幽魂召唤出一个强大的爆炸性能量风暴, 让其用致命的雷电打击弱小的敌人. 风暴的能量来源于闪电幽魂的邪恶意愿, 风暴会自动打击最为弱小的敌方单位, 每次打击非建筑物单位降低其1点护甲. (括号内为装备阿哈利姆神杖时的数值)', '', '', '', '', '', '', '', '500', '20/25/30', '80/70/60', '100/150/200', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (257, 68, '黑暗契约', 1, 0, 'C', '斯拉克与黑暗签下契约, 牺牲自身一部分生命, 1.5秒过后, 一道黑暗之波从它的身体里涌出, 消除身上大部分不利的魔法效果, 同时对自己和周围单位造成伤害. ', '该技能不能用来自杀', '', '', '', '', '', '', '325', '', '9/8/7/6', '55/50/45/40', '', NULL);
INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (258, 68, '突袭', 2, 0, 'E', '突然问到敌人英雄面前, 造成伤害, 并用黑暗锁链牢牢地锁住敌人. 敌人无法逃出这个锁链的作用范围内之外. ', '', '', '', '700', '', '', '', '275', '3.5', '20/16/12/8', '75', '', NULL);
