INSERT INTO `skills` (`id`, `hero_id`, `name`, `ord`, `final`, `hotkey`, `desc`, `addition_1`, `addition_2`, `addition_3`, `cast_dtc`, `max_dtc`, `cast_time`, `cast_delay`, `effect_dtc`, `effect_last`, `cold_down`, `mana`, `advance`, `sub_of`) VALUES (481, 110, '严寒烧灼', 1, 0, 'R', '将致命的寒冷灌注全身，使冰霜巨龙获得额外的攻击距离和飞行能力，持续6秒。冰霜巨龙在攻击敌人时会附带严 寒烧灼，减缓其移动速度并造成6%当前生命值/秒的伤害。', '造成伤害为生命移除，负面效果持续时间在同一敌人身上不会刷新', '', '', '', '', '', '', '', '6', '60/50/40/30', '120/110/100/90', '', NULL);
