DROP TABLE IF EXISTS `skills`;
CREATE TABLE IF NOT EXISTS `skills` ( `id` int(11) PRIMARY KEY NOT NULL, `hero_id` int(11) DEFAULT NULL, `name` varchar(20) NOT NULL, `ord` int(11) DEFAULT NULL, `final` tinyint(1) NOT NULL, `hotkey` varchar(1) DEFAULT NULL, `desc` text, `addition_1` text, `addition_2` text, `addition_3` text, `cast_dtc` varchar(50) DEFAULT NULL, `max_dtc` varchar(50) DEFAULT NULL, `cast_time` varchar(50) DEFAULT NULL, `cast_delay` varchar(50) DEFAULT NULL, `effect_dtc` varchar(50) DEFAULT NULL, `effect_last` varchar(50) DEFAULT NULL, `cold_down` varchar(50) DEFAULT NULL, `mana` varchar(50) DEFAULT NULL, `advance` varchar(100) DEFAULT NULL, `sub_of` int(11) DEFAULT NULL );

